import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, NavLink } from 'react-router-dom';
import { FaVideo, FaSearch, FaList, FaUser, FaMedal } from 'react-icons/fa';
import Home from './components/Home';
import Explore from './components/Explore';
import Notifications from './components/Notifications';
import Messages from './components/Messages';
import Profile from './components/Profile';
import './css/App.css'; // Importez le fichier CSS modifié

const App = () => {
  const [selectedClub, setSelectedClub] = useState(null);

  // Charger le club sélectionné depuis localStorage
  useEffect(() => {
    const savedClub = JSON.parse(localStorage.getItem('selectedClub'));
    if (savedClub) {
      setSelectedClub(savedClub);
    }
  }, []);

  // Fonction pour gérer la sélection du club
  const handleClubSelection = (club) => {
    setSelectedClub(club);
    localStorage.setItem('selectedClub', JSON.stringify(club));
  };

  return (
    <Router>
      <div className="app-container">
        <header className="app-header">
          <div className="selected-club-label">
            {selectedClub ? (
              <span>{selectedClub.name}</span> // Affichage du nom du club sélectionné
            ) : (
              <p>No club selected</p>
            )}
          </div>
        </header>
        <div className="app-content">
          <Routes>
            <Route 
              path="/" 
              element={<Home />} 
            />
            <Route 
              path="/explore" 
              element={<Explore onSelectClub={handleClubSelection} />} 
            />
            <Route 
              path="/notifications" 
              element={<Notifications selectedClub={selectedClub} />} 
            />
            <Route 
              path="/messages" 
              element={<Messages />} 
            />
            <Route 
              path="/profile" 
              element={<Profile />} 
            />
          </Routes>
        </div>
        <nav className="app-navbar">
          <ul>
            <li>
              <NavLink to="/" end activeClassName="active"><FaVideo /></NavLink>
            </li>
            <li>
              <NavLink to="/messages" activeClassName="active"><FaMedal /></NavLink>
            </li>
            <li>
              <NavLink to="/notifications" activeClassName="active"><FaList /></NavLink>
            </li>
            <li>
              <NavLink to="/explore" activeClassName="active"><FaSearch /></NavLink>
            </li>
            <li>
              <NavLink to="/profile" activeClassName="active"><FaUser /></NavLink>
            </li>
          </ul>
        </nav>
      </div>
    </Router>
  );
};

export default App;
