import React, { useState, useEffect } from 'react';
import './css/Home.css';

function Home() {
  const [isRecording, setIsRecording] = useState(false);
  const [timeElapsed, setTimeElapsed] = useState(0);
  const [playbackURI, setPlaybackURI] = useState('');
  const [videoDuration, setVideoDuration] = useState(0);
  const [filename, setFilename] = useState(''); // Nouvel état pour le nom du fichier
  const [message, setMessage] = useState(''); // Nouvel état pour les messages de statut
  const [isLiveView, setIsLiveView] = useState(false); // État pour le flux en direct
  const [liveViewURL, setLiveViewURL] = useState(''); // URL du flux en direct

  useEffect(() => {
    let timer;
    if (isRecording) {
      timer = setInterval(() => {
        setTimeElapsed(prevTime => prevTime + 1);
      }, 1000);
    } else {
      clearInterval(timer);
    }

    return () => clearInterval(timer);
  }, [isRecording]);

  const handleStart = async () => {
    try {
      const response = await fetch('https://ia-sport.oa.r.appspot.com/api/start-recording', {
        method: 'PUT'
      });

      if (!response.ok) {
        throw new Error('Échec du démarrage de l\'enregistrement');
      }

      setIsRecording(true);
      setTimeElapsed(0); // Réinitialiser le chronomètre au démarrage
      setMessage('Enregistrement démarré avec succès');
    } catch (error) {
      console.error('Erreur :', error);
      setMessage('Échec du démarrage de l\'enregistrement');
    }
  };

  const handleStop = async () => {
    try {
      setIsRecording(false);
      const response = await fetch('https://ia-sport.oa.r.appspot.com/api/stop-recording', {
        method: 'PUT'
      });

      if (!response.ok) {
        throw new Error('Échec de l\'arrêt de l\'enregistrement');
      }

      // Récupérer l'URI de lecture et la durée de la vidéo après l'arrêt de l'enregistrement
      const playbackResponse = await fetch('https://ia-sport.oa.r.appspot.com/api/search', {
        method: 'GET'
      });

      if (!playbackResponse.ok) {
        throw new Error('Échec de la récupération de l\'URI de lecture');
      }

      const playbackData = await playbackResponse.json();
      const { playbackURI, videoDuration } = playbackData;
      setPlaybackURI(playbackURI);
      setVideoDuration(videoDuration);

      // Poster les données à l'API d'upload
      const uploadResponse = await fetch('https://ia-sport.oa.r.appspot.com/api/upload', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          filename: `${filename}.mp4`, // Utilisation du nom du fichier dynamique
          cameraRtspUrl: playbackURI,
          directory: 'FC Vidauban',
          duration: videoDuration, // Ajouter la durée de la vidéo ici
        }),
      });

      if (!uploadResponse.ok) {
        throw new Error('Échec du téléchargement de la vidéo');
      }

      setMessage('Enregistrement arrêté et vidéo téléchargée avec succès');
    } catch (error) {
      console.error('Erreur :', error);
      setMessage('Échec de l\'arrêt de l\'enregistrement ou du téléchargement de la vidéo');
    }
  };

  const handleStartLiveView = async () => {
    try {
      const response = await fetch('https://ia-sport.oa.r.appspot.com/api/startLiveView', {
        method: 'GET'
      });

      if (!response.ok) {
        throw new Error('Échec du démarrage du flux en direct');
      }

      // Mettre à jour l'URL du flux en direct
      setLiveViewURL('http://localhost:8080/api/startLiveView');
      setIsLiveView(true);
      setMessage('Flux en direct démarré avec succès');
    } catch (error) {
      console.error('Erreur :', error);
      setMessage('Échec du démarrage du flux en direct');
    }
  };

  const handleStopLiveView = async () => {
    try {
      const response = await fetch('https://ia-sport.oa.r.appspot.com/api/stopLiveView', {
        method: 'GET'
      });

      if (!response.ok) {
        throw new Error('Échec de l\'arrêt du flux en direct');
      }

      setLiveViewURL('');
      setIsLiveView(false);
      setMessage('Flux en direct arrêté avec succès');
    } catch (error) {
      console.error('Erreur :', error);
      setMessage('Échec de l\'arrêt du flux en direct');
    }
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
  };

  return (
    <div>
      <h2>Démarrer un enregistrement</h2>
      <br />

      <div className="input-container">
        <input
          type="text"
          id="filename"
          value={filename}
          onChange={(e) => setFilename(e.target.value)} // Mise à jour du nom de fichier
          placeholder="OM - PSG"
        />
      </div>

      <div className="button-container">
        <button className="btn-start" onClick={handleStart}>Démarrer l'enregistrement</button>
        <button className="btn-stop" onClick={handleStop}>Arrêter l'enregistrement</button>
      </div>

      {isRecording && (
        <div className="timer">
          <p>Temps d'enregistrement : {formatTime(timeElapsed)}</p>
        </div>
      )}

      <h2>Flux en direct</h2>
      <div className="button-container">
        <button className="btn-start" onClick={handleStartLiveView}>Démarrer le flux en direct</button>
        <button className="btn-stop" onClick={handleStopLiveView}>Arrêter le flux en direct</button>
      </div>

      {isLiveView && (
        <div className="video-container">
          <video src={liveViewURL} autoPlay controls style={{ width: '100%', height: 'auto' }} />
        </div>
      )}

      {message && (
        <div className="message">
          <p>{message}</p>
        </div>
      )}
    </div>
  );
}

export default Home;
