import React, { useState, useEffect } from 'react';
import './css/SearchClub.css';

const SearchClub = ({ onSelectClub }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [clubs, setClubs] = useState([]);
  const [selectedClub, setSelectedClub] = useState(null);

  // Fonction pour rechercher les clubs
  const handleSearch = async () => {
    if (searchTerm.trim() === '') {
      setClubs([]); // Réinitialise les clubs si le champ de recherche est vide
      return;
    }

    try {
      const response = await fetch(`https://api-dofa.prd-aws.fff.fr/api/clubs?clNom=${searchTerm}`);
      const data = await response.json();
      const clubData = data['hydra:member'].map(club => ({
        name: club.name,
        logo: club.logo
      }));
      setClubs(clubData);
    } catch (error) {
      console.error('Error fetching clubs:', error);
    }
  };

  // Utiliser useEffect pour appeler handleSearch chaque fois que searchTerm change
  useEffect(() => {
    handleSearch();
  }, [searchTerm]);

  const handleClubClick = (club) => {
    setSelectedClub(club);
    setSearchTerm(club.name); // Met à jour la zone de texte avec le nom du club sélectionné
    onSelectClub(club); // Notifie le parent du club sélectionné
  };

  return (
    <div className="search-club-container">
      <div className="input-container">
        <input
          type="text"
          value={selectedClub ? selectedClub.name : searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search for a club"
          readOnly={!!selectedClub} // Rendre le champ en lecture seule si un club est sélectionné
        />
      </div>
      {!selectedClub && (
        <ul>
          {clubs.map((club, index) => (
            <li key={index} onClick={() => handleClubClick(club)}>
              <img src={club.logo} alt={`${club.name} logo`} />
              <span>{club.name}</span>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SearchClub;
