import React, { useState, useRef, useEffect } from 'react';
import './css/ListeVideo.css';

const ListeVideo = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [videos, setVideos] = useState([]);
  const videoRefs = useRef([]);

  const handleSearch = async () => {
    try {
      const response = await fetch(`https://ia-sport.oa.r.appspot.com/api/videos?folder=${encodeURIComponent(searchTerm)}`);
      const data = await response.json();
      setVideos(data.videos);
    } catch (error) {
      console.error("Erreur lors de la recherche des vidéos:", error);
    }
  };

  const handleDoubleClick = (event, index) => {
    const videoElement = videoRefs.current[index];

    if (videoElement.requestFullscreen) {
      videoElement.requestFullscreen();
    } else if (videoElement.mozRequestFullScreen) { // Firefox
      videoElement.mozRequestFullScreen();
    } else if (videoElement.webkitRequestFullscreen) { // Chrome, Safari and Opera
      videoElement.webkitRequestFullscreen();
    } else if (videoElement.msRequestFullscreen) { // IE/Edge
      videoElement.msRequestFullscreen();
    }
  };

  const handleClick = (event, index) => {
    const videoElement = videoRefs.current[index];
    if (videoElement.paused) {
      videoElement.play();
    } else {
      videoElement.pause();
    }
  };

  useEffect(() => {
    videoRefs.current = videoRefs.current.slice(0, videos.length);
  }, [videos]);

  return (
    <div className="liste-video-container">
      <div className="liste-video-input-container">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search for a folder"
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <ul className="liste-video-ul">
        {videos.map((video, index) => (
          <li key={index} className="liste-video-li">
            <div className="video-wrapper">
              <video
                ref={el => videoRefs.current[index] = el}
                className="liste-video-video"
                src={video.url}
                onClick={(e) => handleClick(e, index)}
                onDoubleClick={(e) => handleDoubleClick(e, index)}
              />
              <input
                type="range"
                className="custom-progress-bar"
                min="0"
                max="100"
                defaultValue="0"
                onChange={(e) => {
                  const videoElement = videoRefs.current[index];
                  const newTime = videoElement.duration * (e.target.value / 100);
                  videoElement.currentTime = newTime;
                }}
                onMouseMove={(e) => {
                  const videoElement = videoRefs.current[index];
                  const progress = (videoElement.currentTime / videoElement.duration) * 100;
                  e.target.value = progress;
                }}
              />
            </div>
            <div className="liste-video-overlay">{video.name}</div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ListeVideo;
