import React, { useState, useEffect } from 'react';
import './css/SearchClub.css';

const SearchClub = ({ onSelectClub }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [clubs, setClubs] = useState([]);
  const [recentClubs, setRecentClubs] = useState([]);
  const [timeoutId, setTimeoutId] = useState(null);

  // Fonction pour rechercher les clubs
  const handleSearch = async () => {
    if (searchTerm.trim() === '') {
      // Affiche les clubs récents lorsque le champ est vide
      setClubs(recentClubs);
      return;
    }

    try {
      const response = await fetch(`https://api-dofa.prd-aws.fff.fr/api/clubs?clNom=${searchTerm}`);
      const data = await response.json();
      const clubData = data['hydra:member'].map(club => ({
        name: club.name,
        logo: club.logo
      }));

      // Limiter les résultats à 3
      const limitedClubData = clubData.slice(0, 3);
      setClubs(limitedClubData);
    } catch (error) {
      console.error('Error fetching clubs:', error);
    }
  };

  // Utiliser useEffect pour appeler handleSearch avec délai chaque fois que searchTerm change
  useEffect(() => {
    // Si un ancien timeout existe, on le nettoie
    if (timeoutId) {
      clearTimeout(timeoutId);
    }

    // Créer un nouveau timeout
    const id = setTimeout(() => {
      handleSearch();
    }, 750); 

    // Sauvegarder l'identifiant du timeout pour pouvoir l'annuler si nécessaire
    setTimeoutId(id);

    // Nettoyage de l'effet
    return () => clearTimeout(id);
  }, [searchTerm]);

  // Charger les clubs récents depuis localStorage au démarrage
  useEffect(() => {
    const savedRecentClubs = JSON.parse(localStorage.getItem('recentClubs')) || [];
    setRecentClubs(savedRecentClubs);
  }, []);

  // Fonction pour mettre à jour localStorage et l'état des clubs récents
  const updateRecentClubs = (club) => {
    const updatedClubs = [club, ...recentClubs.filter(c => c.name !== club.name)].slice(0, 5);
    setRecentClubs(updatedClubs);
    localStorage.setItem('recentClubs', JSON.stringify(updatedClubs));
  };

  const handleClubClick = (club) => {
    setSearchTerm(""); 
    onSelectClub(club); // Notifie le parent du club sélectionné
    updateRecentClubs(club); // Met à jour la liste des clubs récents
  };

  return (
    <div className="search-club-container">
      <div className="input-container">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search for a club"
        />
      </div>
      <ul>
        {clubs.length === 0 && searchTerm === '' ? (
          // Affiche les clubs récents si la zone de texte est vide
          recentClubs.map((club, index) => (
            <li key={index} onClick={() => handleClubClick(club)}>
              <img src={club.logo} alt={`${club.name} logo`} />
              <span>{club.name}</span>
            </li>
          ))
        ) : (
          // Affiche les clubs de la recherche, limités à 3
          clubs.map((club, index) => (
            <li key={index} onClick={() => handleClubClick(club)}>
              <img src={club.logo} alt={`${club.name} logo`} />
              <span>{club.name}</span>
            </li>
          ))
        )}
      </ul>
    </div>
  );
};

export default SearchClub;
