import React, { useRef, useState } from 'react';
import './css/ListeVideo.css';

const ListeVideo = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [videos, setVideos] = useState([]);
  const videoRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);

  const handleSearch = async () => {
    try {
      const response = await fetch(`https://ia-sport.oa.r.appspot.com/api/videos?folder=${encodeURIComponent(searchTerm)}`);
      const data = await response.json();
      setVideos(data.videos);
    } catch (error) {
      console.error("Erreur lors de la recherche des vidéos:", error);
    }
  };

  const togglePlay = () => {
    if (videoRef.current.paused) {
      videoRef.current.play();
      setIsPlaying(true);
    } else {
      videoRef.current.pause();
      setIsPlaying(false);
    }
  };

  const handleFullScreen = () => {
    if (videoRef.current.requestFullscreen) {
      videoRef.current.requestFullscreen();
    } else if (videoRef.current.mozRequestFullScreen) { /* Firefox */
      videoRef.current.mozRequestFullScreen();
    } else if (videoRef.current.webkitRequestFullscreen) { /* Chrome, Safari & Opera */
      videoRef.current.webkitRequestFullscreen();
    } else if (videoRef.current.msRequestFullscreen) { /* IE/Edge */
      videoRef.current.msRequestFullscreen();
    }
  };

  return (
    <div className="liste-video-container">
      <div className="liste-video-input-container">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search for a folder"
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <ul className="liste-video-ul">
        {videos.map((video, index) => (
          <li key={index} className="liste-video-li">
            <video ref={videoRef} className="liste-video-video">
              <source src={video} type="video/mp4" />
              <p>Your browser doesn't support HTML5 video. Here is a <a href={video}>link to the video</a> instead.</p>
            </video>
            <div className="liste-video-overlay">{video}</div>
            <div className="custom-controls">
              <button onClick={togglePlay}>
                {isPlaying ? 'Pause' : 'Play'}
              </button>
              <button onClick={handleFullScreen}>Full Screen</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ListeVideo;
