import React, { useState, useEffect } from 'react';
import { fetchClassementJournees } from './../api'; // Importer la fonction depuis votre fichier API
import './css/ClassementsContent.css';

function ClassementsContent() {
  const [classements, setClassements] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const competitionId = 426177;
        const phaseId = 1;
        const pouleId = 2;

        const data = await fetchClassementJournees(competitionId, phaseId, pouleId);
        setClassements(data);
        setLoading(false);
      } catch (error) {
        setError(error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Chargement des classements...</div>;
  }

  if (error) {
    return <div>Erreur lors du chargement des classements : {error.message}</div>;
  }

  return (
    <div className="classement-content">
      {classements.length > 0 ? (
        <table className="classement-table">
          <thead>
            <tr>
              <th className="team-column">Équipe</th> {/* Classe ajoutée ici */}
              <th>Points</th>
              <th>Joués</th>
              <th>Gagnés</th>
              <th>Nuls</th>
              <th>Perdus</th>
              <th>Buts Pour</th>
              <th>Buts Contre</th>
              <th>Différence de Buts</th>
            </tr>
          </thead>
          <tbody>
            {classements.map((journee, index) => (
              <tr key={index}>
                <td>{journee.teamName}</td>
                <td>{journee.points}</td>
                <td>{journee.totalGames}</td>
                <td>{journee.wonGames}</td>
                <td>{journee.drawGames}</td>
                <td>{journee.lostGames}</td>
                <td>{journee.goalsFor}</td>
                <td>{journee.goalsAgainst}</td>
                <td>{journee.goalDifference}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="error">Aucun classement disponible pour le moment.</p>
      )}
    </div>
  );
}

export default ClassementsContent;
