import React from 'react';
import { Button, Alert } from 'react-native';
import { GoogleSignin, statusCodes } from '@react-native-google-signin/google-signin';
import './css/Profile.css'; // Vous pouvez garder le CSS si vous utilisez également une version web

GoogleSignin.configure({
  webClientId: '417232013163-v2genb8j1f3odhrjt40hm9fsghgodgrl.apps.googleusercontent.com', // Utilisez votre client ID web ici
  offlineAccess: true,
});

const Profile = () => {
  const signIn = async () => {
    try {
      await GoogleSignin.hasPlayServices();
      const userInfo = await GoogleSignin.signIn();
      console.log('Login Success:', userInfo);

      // Envoyer le token au backend pour vérification
      const response = await fetch('https://api-dofa.prd-aws.fff.fr/api/auth/google', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          idToken: userInfo.idToken,
        }),
      });
      const data = await response.json();
      console.log('Backend response:', data);
    } catch (error) {
      if (error.code === statusCodes.SIGN_IN_CANCELLED) {
        Alert.alert('Connexion annulée');
      } else if (error.code === statusCodes.IN_PROGRESS) {
        Alert.alert('Connexion en cours');
      } else if (error.code === statusCodes.PLAY_SERVICES_NOT_AVAILABLE) {
        Alert.alert('Google Play Services non disponible');
      } else {
        Alert.alert('Erreur de connexion', error.message);
      }
    }
  };

  return (
    <div className="profile-page">
      <div className="profile-container">
        <h1>Connectez-vous à votre compte</h1>

        {/* Email Input */}
        <div className="input-group">
          <label htmlFor="email">Adresse e-mail</label>
          <div className="inputs">
            <input type="email" id="email" placeholder="Entrez votre email" />
          </div>
        </div>

        {/* Password Input */}
        <div className="input-group">
          <label htmlFor="password">Mot de passe</label>
          <div className="inputs">
            <input type="password" id="password" placeholder="Entrez votre mot de passe" />
          </div>
        </div>

        {/* Login Button */}
        <div className="login-btn">Se connecter</div>

        {/* Divider */}
        <div className="divider">
          <span>ou</span>
        </div>

        {/* Google SignIn Button for Android */}
        <Button title="Se connecter avec Google" onPress={signIn} />
      </div>
    </div>
  );
};

export default Profile;
