import React, { useState, useEffect, useCallback } from 'react';
import './css/ListeVideo.css';
import { getSelectedClub } from './../../config'; // Importer la fonction pour obtenir le club sélectionné

const ListeVideo = () => {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null); // Vidéo sélectionnée
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('VIDEO'); // Onglet actif
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false); // État de la sidebar

  // Fonction pour rechercher les vidéos
  const handleSearch = useCallback(async () => {
    setLoading(true);
    setError(null);
    const club = getSelectedClub();
    const folder = club ? club.name : '';
    const url = `https://ia-sport.oa.r.appspot.com/api/videos?folder=${encodeURIComponent(folder)}`;

    if (folder) {
      try {
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error('Erreur lors de la récupération des vidéos');
        }
        const data = await response.json();
        setVideos(data.videos || []);
        setSelectedVideo(data.videos[0] || null); // Sélectionner la première vidéo par défaut
      } catch (error) {
        setError('Impossible de charger les vidéos. Veuillez réessayer plus tard.');
        console.error("Erreur lors de la recherche des vidéos:", error);
        setVideos([]);
      } finally {
        setLoading(false);
      }
    } else {
      setVideos([]);
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    handleSearch();
  }, [handleSearch]);

  const handleVideoSelect = (video) => {
    setSelectedVideo(video);
  };

  const handleDoubleClick = (event) => {
    const videoElement = event.target;

    if (videoElement.requestFullscreen) {
      videoElement.requestFullscreen();
    } else if (videoElement.mozRequestFullScreen) { // Firefox
      videoElement.mozRequestFullScreen();
    } else if (videoElement.webkitRequestFullscreen) { // Chrome, Safari and Opera
      videoElement.webkitRequestFullscreen();
    } else if (videoElement.msRequestFullscreen) { // IE/Edge
      videoElement.msRequestFullscreen();
    }
  };

  const handleClick = (event) => {
    const videoElement = event.target;
    if (videoElement.paused) {
      videoElement.play();
    } else {
      videoElement.pause();
    }
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  const toggleSidebar = () => {
    setIsSidebarCollapsed(!isSidebarCollapsed);
  };

  return (
    <div className="liste-video-container">
      {loading && <div className="loader">Chargement...</div>}
      {error && <div className="error-message">{error}</div>}
      {!loading && !error && (
        <div className="liste-video-wrapper">
          {/* Bouton de rétraction */}
          <button 
            className={`liste-video-sidebar-toggle ${isSidebarCollapsed ? 'closed' : 'open'}`}
            onClick={toggleSidebar}
          >
            {isSidebarCollapsed ? '▶' : '◀'} {/* Symbole pour indiquer l'état */}
          </button>

          {/* Colonne de gauche: Liste des titres de vidéos */}
          <div className={`liste-video-sidebar ${isSidebarCollapsed ? 'sidebar-collapsed' : ''}`}>
            <ul className="liste-video-titles">
              {videos.length > 0 ? (
                videos.map((video, index) => (
                  <li
                    key={index}
                    className={`liste-video-title ${selectedVideo === video ? 'selected' : ''}`}
                    onClick={() => handleVideoSelect(video)}
                  >
                    {video.name}
                  </li>
                ))
              ) : (
                <p className="no-videos">Aucune vidéo disponible</p>
              )}
            </ul>
          </div>

          {/* Colonne de droite: Contenu avec onglets */}
          <div className="liste-video-content">
            {/* Menu avec onglets */}
            <nav className="tabs">
              <button
                className={activeTab === 'VIDEO' ? 'active' : ''}
                onClick={() => handleTabClick('VIDEO')}
              >
                Vidéo
              </button>
              <button
                className={activeTab === 'DESCRIPTION' ? 'active' : ''}
                onClick={() => handleTabClick('DESCRIPTION')}
              >
                Description
              </button>
              <button
                className={activeTab === 'COMMENTAIRES' ? 'active' : ''}
                onClick={() => handleTabClick('COMMENTAIRES')}
              >
                Commentaires
              </button>
              <button
                className={activeTab === 'AUTRES' ? 'active' : ''}
                onClick={() => handleTabClick('AUTRES')}
              >
                Autres
              </button>
              {/* Ajoutez d'autres onglets ici si nécessaire */}
            </nav>

            {/* Contenu des onglets */}
            <div className="tab-content">
              {activeTab === 'VIDEO' && selectedVideo ? (
                <video 
                  className="liste-video-content-video" 
                  src={selectedVideo.url} 
                  controls
                  onClick={handleClick}
                  onDoubleClick={handleDoubleClick}
                >
                  <p>Your browser doesn't support HTML5 video. Here is a <a href={selectedVideo.url}>link to the video</a> instead.</p>
                </video>
              ) : activeTab === 'DESCRIPTION' ? (
                <div className="video-description">
                  <h3>Description</h3>
                  <p>{selectedVideo ? selectedVideo.description : 'Aucune description disponible'}</p>
                </div>
              ) : activeTab === 'COMMENTAIRES' ? (
                <div className="video-comments">
                  <h3>Commentaires</h3>
                  <p>Commentaires ici...</p> {/* Remplacez par la logique de commentaire si disponible */}
                </div>
              ) : activeTab === 'AUTRES' ? (
                <div className="video-others">
                  <h3>Autres Informations</h3>
                  <p>Autres informations ici...</p> {/* Remplacez par d'autres contenus si disponible */}
                </div>
              ) : null}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ListeVideo;
