import React, { useState, useEffect } from 'react';
import { fetchMatchesForClub } from './api'; // Assurez-vous que le chemin d'importation est correct

function MatchsContent({ cl_no }) {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadMatches = async () => {
      try {
        const data = await fetchMatchesForClub(cl_no);
        setMatches(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    loadMatches();
  }, [cl_no]);

  if (loading) {
    return <div className="tab-content">Chargement des matchs...</div>;
  }

  if (error) {
    return <div className="tab-content">Erreur : {error}</div>;
  }

  return (
    <div className="tab-content">
      <h2>Matchs</h2>
      {matches.length === 0 ? (
        <p>Aucun match trouvé.</p>
      ) : (
        <ul>
          {matches.map(match => (
            <li key={match.id} className="match-item">
              <h3>Compétition: {match.competition}</h3>
              <p><strong>Phase:</strong> {match.phase}</p>
              <p><strong>Poule:</strong> {match.poule}</p>
              <p><strong>Jour de Poule:</strong> {match.pouleJournee}</p>
              <p><strong>Équipe à Domicile:</strong> {match.homeTeam}</p>
              <p><strong>Équipe Adverse:</strong> {match.opponentTeam}</p>
              <p><strong>Date:</strong> {match.date}</p>
              <p><strong>Score:</strong> {match.score}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default MatchsContent;
