import React, { useState, useEffect, useCallback } from 'react';
import './css/ListeVideo.css';
import { getSelectedClub } from './../../config'; // Importer la fonction pour obtenir le club sélectionné

const ListeVideo = () => {
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fonction pour rechercher les vidéos
  const handleSearch = useCallback(async () => {
    setLoading(true);
    setError(null);
    const club = getSelectedClub();
    const folder = club ? club.name : '';
    const url = `https://ia-sport.oa.r.appspot.com/api/videos?folder=${encodeURIComponent(folder)}`;

    if (folder) {
      try {
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error('Erreur lors de la récupération des vidéos');
        }
        const data = await response.json();
        setVideos(data.videos || []);
      } catch (error) {
        setError('Impossible de charger les vidéos. Veuillez réessayer plus tard.');
        console.error("Erreur lors de la recherche des vidéos:", error);
        setVideos([]);
      } finally {
        setLoading(false);
      }
    } else {
      setVideos([]);
      setLoading(false);
    }
  }, [getSelectedClub]);

  useEffect(() => {
    handleSearch();
  }, [handleSearch]);

  const handleDoubleClick = (event) => {
    const videoElement = event.target;

    if (videoElement.requestFullscreen) {
      videoElement.requestFullscreen();
    } else if (videoElement.mozRequestFullScreen) { // Firefox
      videoElement.mozRequestFullScreen();
    } else if (videoElement.webkitRequestFullscreen) { // Chrome, Safari and Opera
      videoElement.webkitRequestFullscreen();
    } else if (videoElement.msRequestFullscreen) { // IE/Edge
      videoElement.msRequestFullscreen();
    }
  };

  const handleClick = (event) => {
    const videoElement = event.target;
    if (videoElement.paused) {
      videoElement.play();
    } else {
      videoElement.pause();
    }
  };

  return (
    <div className="liste-video-container">
      {loading && <div className="loader">Chargement...</div>}
      {error && <div className="error-message">{error}</div>}
      {!loading && !error && (
        <ul className="liste-video-ul">
          {videos.length > 0 ? (
            videos.map((video, index) => (
              <li key={index} className="liste-video-li">
                <video 
                  className="liste-video-video" 
                  src={video.url} 
                  controls
                  onClick={handleClick}
                  onDoubleClick={handleDoubleClick}
                >
                  <p>Your browser doesn't support HTML5 video. Here is a <a href={video.url}>link to the video</a> instead.</p>
                </video>
                <div className="liste-video-overlay">{video.name}</div>
              </li>
            ))
          ) : (
            <p className="no-videos">Aucune vidéo disponible</p>
          )}
        </ul>
      )}
    </div>
  );
};

export default ListeVideo;
