// src/components/Profile.js
import React, { useEffect } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from 'react-native';
import { GoogleSignin, statusCodes } from '@react-native-google-signin/google-signin';

const Profile = () => {
  useEffect(() => {
    GoogleSignin.configure({
      webClientId: 'YOUR_WEB_CLIENT_ID.apps.googleusercontent.com', // Remplacez par votre Client ID
      offlineAccess: true,
    });
  }, []);

  const handleGoogleLogin = async () => {
    try {
      await GoogleSignin.hasPlayServices(); // Vérifiez les services Google Play
      const userInfo = await GoogleSignin.signIn(); // Démarrez la connexion
      console.log('Utilisateur connecté:', userInfo);
      
      // Ici, vous pouvez envoyer le token ID au backend
      const idToken = userInfo.idToken;
      // const data = await sendIdTokenToBackend(idToken);
      // console.log('Utilisateur vérifié avec succès:', data);
    } catch (error) {
      if (error.code === statusCodes.SIGN_IN_CANCELLED) {
        console.log('Connexion annulée');
      } else if (error.code === statusCodes.IN_PROGRESS) {
        console.log('Connexion en cours');
      } else if (error.code === statusCodes.PLAY_SERVICES_NOT_AVAILABLE) {
        console.log('Services Google Play non disponibles');
      } else {
        console.error('Erreur lors de la connexion Google:', error);
      }
    }
  };

  return (
    <View style={styles.profileContainer}>
      <Text style={styles.title}>Connectez-vous à votre compte</Text>

      {/* Email Input */}
      <View style={styles.inputGroup}>
        <Text style={styles.label}>Adresse e-mail</Text>
        <TextInput style={styles.input} placeholder="Entrez votre email" />
      </View>

      {/* Password Input */}
      <View style={styles.inputGroup}>
        <Text style={styles.label}>Mot de passe</Text>
        <TextInput style={styles.input} placeholder="Entrez votre mot de passe" secureTextEntry />
      </View>

      {/* Login Button */}
      <TouchableOpacity style={styles.loginButton}>
        <Text style={styles.loginButtonText}>Se connecter</Text>
      </TouchableOpacity>

      {/* Divider */}
      <View style={styles.divider}>
        <Text>ou</Text>
      </View>

      {/* Google Login Button */}
      <TouchableOpacity style={styles.googleButton} onPress={handleGoogleLogin}>
        <Text style={styles.googleButtonText}>Se connecter avec Google</Text>
      </TouchableOpacity>
    </View>
  );
};

// Styles
const styles = StyleSheet.create({
  profileContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  title: {
    fontSize: 24,
    marginBottom: 20,
  },
  inputGroup: {
    width: '100%',
    marginBottom: 15,
  },
  label: {
    marginBottom: 5,
  },
  input: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    padding: 10,
  },
  loginButton: {
    backgroundColor: '#007bff',
    padding: 15,
    borderRadius: 5,
    alignItems: 'center',
    marginBottom: 15,
  },
  loginButtonText: {
    color: '#fff',
    fontSize: 16,
  },
  divider: {
    marginVertical: 15,
  },
  googleButton: {
    backgroundColor: '#db4437',
    padding: 15,
    borderRadius: 5,
    alignItems: 'center',
    width: '100%',
  },
  googleButtonText: {
    color: '#fff',
    fontSize: 16,
  },
});

export default Profile;
