import React, { useEffect } from 'react';
import { View, Text, TextInput, TouchableOpacity } from 'react-native';
import { GoogleSignin } from '@react-native-google-signin/google-signin'; // Correct import
import { sendIdTokenToBackend } from './api'; // Adjust this path as needed
import './css/Profile.css'; // CSS files may not apply, consider using StyleSheet in React Native

const Profile = () => {
  useEffect(() => {
    GoogleSignin.configure({
      webClientId: 'YOUR_WEB_CLIENT_ID.apps.googleusercontent.com', // Replace with your web client ID
    });
  }, []);

  const handleGoogleLoginSuccess = async (userInfo) => {
    const idToken = userInfo.idToken; // Assuming userInfo contains idToken

    try {
      const data = await sendIdTokenToBackend(idToken);
      console.log('Utilisateur vérifié avec succès:', data);
    } catch (error) {
      console.error('Erreur lors de la vérification du token Google:', error);
    }
  };

  const handleGoogleLoginError = (error) => {
    console.error('Erreur de connexion Google:', error);
  };

  const signInWithGoogle = async () => {
    try {
      const userInfo = await GoogleSignin.signIn();
      handleGoogleLoginSuccess(userInfo);
    } catch (error) {
      handleGoogleLoginError(error);
    }
  };

  return (
    <View style={{ padding: 20 }}>
      <Text style={{ fontSize: 24 }}>Connectez-vous à votre compte</Text>

      {/* Email Input */}
      <View style={{ marginVertical: 10 }}>
        <Text>Adresse e-mail</Text>
        <TextInput style={{ borderWidth: 1, padding: 10 }} placeholder="Entrez votre email" />
      </View>

      {/* Password Input */}
      <View style={{ marginVertical: 10 }}>
        <Text>Mot de passe</Text>
        <TextInput style={{ borderWidth: 1, padding: 10 }} secureTextEntry placeholder="Entrez votre mot de passe" />
      </View>

      {/* Login Button */}
      <TouchableOpacity style={{ padding: 10, backgroundColor: 'blue', borderRadius: 5 }}>
        <Text style={{ color: 'white', textAlign: 'center' }}>Se connecter</Text>
      </TouchableOpacity>

      {/* Divider */}
      <View style={{ marginVertical: 10, alignItems: 'center' }}>
        <Text>ou</Text>
      </View>

      {/* Google Login for Mobile */}
      <TouchableOpacity style={{ padding: 10, backgroundColor: 'red', borderRadius: 5 }} onPress={signInWithGoogle}>
        <Text style={{ color: 'white', textAlign: 'center' }}>Se connecter avec Google</Text>
      </TouchableOpacity>
    </View>
  );
};

export default Profile;
