// App.js

import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, NavLink, useLocation } from 'react-router-dom';
import { FaVideo, FaSearch, FaList, FaUser, FaMedal } from 'react-icons/fa'; // Importez les icônes nécessaires
import Home from './components/Home';
import Explore from './components/Explore';
import Notifications from './components/Notifications';
import Messages from './components/Messages';
import Profile from './components/Profile';
import './App.css'; // Importez votre fichier CSS ici

const App = () => {
  const [prevPath, setPrevPath] = useState(null);
  const location = useLocation();

  useEffect(() => {
    setPrevPath(location.pathname);
  }, [location]);

  const getClassName = (path) => {
    if (prevPath === path) return 'active';
    if (location.pathname === path) {
      if (prevPath && prevPath < location.pathname) return 'active next';
      if (prevPath && prevPath > location.pathname) return 'active previous';
      return 'active';
    }
    return '';
  };

  return (
    <Router>
      <div className="App">
        <div className="content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/explore" element={<Explore />} />
            <Route path="/notifications" element={<Notifications />} />
            <Route path="/messages" element={<Messages />} />
            <Route path="/profile" element={<Profile />} />
          </Routes>
        </div>

        <nav className="navbar">
          <ul>
            <li>
              <NavLink to="/" className={getClassName('/')}><FaVideo /></NavLink>
            </li>
            <li>
              <NavLink to="/explore" className={getClassName('/explore')}><FaMedal /></NavLink>
            </li>
            <li>
              <NavLink to="/notifications" className={getClassName('/notifications')}><FaList /></NavLink>
            </li>
            <li>
              <NavLink to="/messages" className={getClassName('/messages')}><FaSearch /></NavLink>
            </li>
            <li>
              <NavLink to="/profile" className={getClassName('/profile')}><FaUser /></NavLink>
            </li>
          </ul>
        </nav>
      </div>
    </Router>
  );
};

export default App;
