// src/components/Profile.js
import React, { useEffect } from 'react';
import { GoogleSignin } from '@react-native-google-signin/google-signin'; // Pour React Native
import { sendIdTokenToBackend } from './api'; // Chemin vers votre fichier API
import './css/Profile.css';

const Profile = () => {
  useEffect(() => {
    GoogleSignin.configure({
      webClientId: 'YOUR_WEB_CLIENT_ID', // Remplacez par votre ID de client web
    });
  }, []);

  const handleGoogleLogin = async () => {
    try {
      // Débuter la connexion
      const userInfo = await GoogleSignin.signIn();
      console.log('Utilisateur connecté:', userInfo);

      // Envoyer le token d'identification au backend
      const idToken = userInfo.idToken; // Vous pouvez aussi utiliser userInfo.user
      const data = await sendIdTokenToBackend(idToken);
      console.log('Utilisateur vérifié avec succès:', data);
    } catch (error) {
      console.error('Erreur lors de la connexion avec Google:', error);
    }
  };

  return (
    <div className="profile-page">
      <div className="profile-container">
        <h1>Connectez-vous à votre compte</h1>

        {/* Email Input */}
        <div className="input-group">
          <label htmlFor="email">Adresse e-mail</label>
          <div className="inputs">
            <input type="email" id="email" placeholder="Entrez votre email" />
          </div>
        </div>

        {/* Password Input */}
        <div className="input-group">
          <label htmlFor="password">Mot de passe</label>
          <div className="inputs">
            <input type="password" id="password" placeholder="Entrez votre mot de passe" />
          </div>
        </div>

        {/* Login Button */}
        <div className="login-btn" onClick={handleGoogleLogin}>
          Se connecter avec Googles
        </div>
      </div>
    </div>
  );
};

export default Profile;
