import React, { useState, useEffect, useCallback } from 'react';
import './css/ListeVideoSidebar.css';
import { getSelectedClub } from './../../config';

const ListeVideoSidebar = () => {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('VIDEO');

  const handleSearch = useCallback(async () => {
    setLoading(true);
    setError(null);
    const club = getSelectedClub();
    const folder = club ? club.name : '';
    const url = `https://ia-sport.oa.r.appspot.com/api/videos?folder=${encodeURIComponent(folder)}`;

    if (folder) {
      try {
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error('Erreur lors de la récupération des vidéos');
        }
        const data = await response.json();
        setvideoSidebars(data.videoSidebars || []);
        setSelectedvideoSidebar(data.videoSidebars[0] || null);
      } catch (error) {
        setError('Impossible de charger les vidéos. Veuillez réessayer plus tard.');
        console.error("Erreur lors de la recherche des vidéos:", error);
        setvideoSidebars([]);
      } finally {
        setLoading(false);
      }
    } else {
      setvideoSidebars([]);
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    handleSearch();
  }, [handleSearch]);

  const handlevideoSidebarSelect = (videoSidebar) => {
    setSelectedvideoSidebar(videoSidebar);
  };

  const handleDoubleClick = (event) => {
    const videoSidebarElement = event.target;

    if (videoSidebarElement.requestFullscreen) {
      videoSidebarElement.requestFullscreen();
    } else if (videoSidebarElement.mozRequestFullScreen) {
      videoSidebarElement.mozRequestFullScreen();
    } else if (videoSidebarElement.webkitRequestFullscreen) {
      videoSidebarElement.webkitRequestFullscreen();
    } else if (videoSidebarElement.msRequestFullscreen) {
      videoSidebarElement.msRequestFullscreen();
    }
  };

  const handleClick = (event) => {
    const videoSidebarElement = event.target;
    if (videoSidebarElement.paused) {
      videoSidebarElement.play();
    } else {
      videoSidebarElement.pause();
    }
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="liste-videoSidebar-container">
      {loading && <div className="loader">Chargement...</div>}
      {error && <div className="error-message">{error}</div>}
      {!loading && !error && (
        <div className="liste-videoSidebar-wrapper">
          <div className="liste-videoSidebar-sidebar">
            <ul className="liste-videoSidebar-titles">
              {videoSidebars.length > 0 ? (
                videoSidebars.map((videoSidebar, index) => (
                  <li
                    key={index}
                    className={`liste-videoSidebar-title ${selectedvideoSidebar === videoSidebar ? 'selected' : ''}`}
                    onClick={() => handlevideoSidebarSelect(videoSidebar)}
                  >
                    {videoSidebar.name}
                  </li>
                ))
              ) : (
                <p className="no-videoSidebars">Aucune vidéo disponible</p>
              )}
            </ul>
          </div>

        </div>
      )}
    </div>
  );
};

export default ListevideoSidebarSidebar;
