// src/components/Video/ListeVideoSidebar.js

import React from 'react';
import './css/ListeVideo.css';

const ListeVideoSidebar = ({ videos, selectedVideo, onVideoSelect }) => {
  return (
    <div className="liste-video-sidebar">
      <ul className="liste-video-titles">
        {videos.length > 0 ? (
          videos.map((video, index) => (
            <li
              key={index}
              className={`liste-video-title ${selectedVideo === video ? 'selected' : ''}`}
              onClick={() => onVideoSelect(video)}
            >
              {video.name}
            </li>
          ))
        ) : (
          <p className="no-videos">Aucune vid√©o disponible</p>
        )}
      </ul>
    </div>
  );
};

export default ListeVideoSidebar;
