// src/components/Profile.js
import React, { useEffect } from 'react';
import { View, Text, TextInput, TouchableOpacity, Platform } from 'react-native'; // Composants React Native
import { GoogleLogin } from '@react-oauth/google'; // Utilisation pour le web
import { GoogleSignin } from '@react-native-google-signin/google-signin'; // Utilisation pour mobile
import { sendIdTokenToBackend } from './api'; // Chemin vers votre fichier API
import './css/Profile.css'; // Incluez vos styles CSS

const Profile = () => {
  useEffect(() => {
    if (Platform.OS !== 'web') {
      GoogleSignin.configure({
        webClientId: 'YOUR_WEB_CLIENT_ID', // Remplacez par votre ID de client web
      });
    }
  }, []);

  const handleGoogleLoginSuccess = async (response) => {
    const idToken = response.credential; // Pour le web

    try {
      const data = await sendIdTokenToBackend(idToken);
      console.log('Utilisateur vérifié avec succès:', data);
    } catch (error) {
      console.error('Erreur lors de la vérification du token Google:', error);
    }
  };

  const handleGoogleLoginMobile = async () => {
    try {
      const userInfo = await GoogleSignin.signIn();
      console.log('Utilisateur connecté:', userInfo);

      // Envoyer le token d'identification au backend
      const idToken = userInfo.idToken; // Utilisez idToken pour mobile
      const data = await sendIdTokenToBackend(idToken);
      console.log('Utilisateur vérifié avec succès:', data);
    } catch (error) {
      console.error('Erreur lors de la connexion avec Google:', error);
    }
  };

  return (
    <View className="profile-page">
      <Text className="profile-container">Connectez-vous à votre compte</Text>

      {/* Email Input */}
      <View className="input-group">
        <Text htmlFor="email">Adresse e-mail</Text>
        <TextInput 
          id="email" 
          placeholder="Entrez votre email" 
          style={{ borderWidth: 1, marginBottom: 10 }} 
        />
      </View>

      {/* Password Input */}
      <View className="input-group">
        <Text htmlFor="password">Mot de passe</Text>
        <TextInput 
          id="password" 
          placeholder="Entrez votre mot de passe" 
          secureTextEntry 
          style={{ borderWidth: 1, marginBottom: 10 }} 
        />
      </View>

      {/* Login Button */}
      <TouchableOpacity style={{ marginBottom: 10 }}>
        <Text>Se connecter</Text>
      </TouchableOpacity>

      {/* Divider */}
      <Text>ou</Text>

      {/* Google Login */}
      {Platform.OS === 'web' ? (
        <div className="login-btn">
          <GoogleLogin
            onSuccess={handleGoogleLoginSuccess}
            onError={(response) => console.error('Login Failure:', response)}
          />
        </div>
      ) : (
        <TouchableOpacity onPress={handleGoogleLoginMobile}>
          <Text>Se connecter avec Google</Text>
        </TouchableOpacity>
      )}
    </View>
  );
};

export default Profile;
