import React, { useState, useEffect, useRef, useMemo } from 'react';
import { fetchMatchesForClub } from './../api'; // Assurez-vous que le chemin d'importation est correct
import './css/MatchsContent.css'; // Importation du CSS
import config from '../../config';  // Import du fichier config.js

function MatchsContent({ cl_no }) {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const matchRefs = useRef([]);
  const selectedClub = config.getSelectedClub();  // Récupérer le club sélectionné directement
  const selectedCompetition = config.getSelectedCompetition();  // Récupérer la compétition sélectionnée

  useEffect(() => {
    const loadMatches = async () => {
      setLoading(true);
      try {
        const data = await fetchMatchesForClub(selectedClub.cl_no);
        setMatches(data);
      } catch (error) {
        setError('Erreur lors de la récupération des matchs.');
      } finally {
        setLoading(false);
      }
    };

    if (selectedClub.cl_no) {  // Vérifier si le club est sélectionné avant d'effectuer l'appel
      loadMatches();
    }
  }, [selectedClub.cl_no]);

  const filteredMatches = useMemo(() => {
    return matches.filter(
      match =>
        (match.homeTeam === selectedClub.name && match.homeCompetitionName === selectedCompetition) ||
        (match.awayTeam === selectedClub.name && match.awayCompetitionName === selectedCompetition)
    );
  }, [matches, selectedClub.name, selectedCompetition]);

  useEffect(() => {
    if (filteredMatches.length > 0) {
      const today = new Date();
      const latestMatchIndex = filteredMatches.reduce((latestIndex, match, index) => {
        const matchDate = new Date(match.date);
        return matchDate < today && (latestIndex === -1 || matchDate > new Date(filteredMatches[latestIndex].date))
          ? index
          : latestIndex;
      }, -1);

      if (latestMatchIndex >= 0 && matchRefs.current[latestMatchIndex]) {
        matchRefs.current[latestMatchIndex].scrollIntoView({
          behavior: 'smooth',
          block: 'start',
        });
      }
    }
  }, [filteredMatches]);

  if (loading) {
    return <div className="loading">Chargement des matchs...</div>;
  }

  if (error) {
    return <div className="error">Erreur : {error}</div>;
  }

  return (
    <div className="tab-content">
      {filteredMatches.length === 0 ? (
        <p className="error">Aucun match trouvé pour {selectedClub.name} à domicile.</p>
      ) : (
        <div>
          {filteredMatches.map((match, index) => (
            <div
              key={match.id}
              className="match-item"
              ref={el => matchRefs.current[index] = el}
            >
              <div className="match-date">
                {new Date(match.date).toLocaleDateString()} - {match.time} - {match.competitionName}
              </div>
              <div className="match-content">
                <div className="match-details-team">
                  <img src={match.homeLogo} alt={`${match.homeTeam} logo`} className="team-logo" />
                  {match.homeTeam}
                </div>
                <div className="match-score">
                  {match.home_score}
                </div>
                <div className="match-details-team">
                  <img src={match.awayLogo} alt={`${match.awayTeam} logo`} className="team-logo" />
                  {match.awayTeam}
                </div>
                <div className="match-score">
                  {match.away_score}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default MatchsContent;
