import React, { useState } from 'react';
import './css/ListeVideo.css';

const ListeVideo = ({ selectedVideo, resumeVideoUrl }) => {
  const [activeTab, setActiveTab] = useState('VIDEO');
  const [shareVisible, setShareVisible] = useState(false);

  const handleSave = () => {
    console.log("Données enregistrées !");
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  const handleDownload = () => {
    const link = document.createElement('a');
    link.href = 'URL_DU_FICHIER_A_TELECHARGER'; // Remplacez par l'URL de votre fichier
    link.download = 'NOM_DU_FICHIER.extension'; // Remplacez par le nom souhaité du fichier
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const toggleShareButtons = () => {
    setShareVisible((prev) => !prev);
  };

  // Fonction pour partager sur un réseau social
  const shareOnSocialMedia = (platform) => {
    let urlToShare;
    if (activeTab === 'VIDEO' && selectedVideo) {
      urlToShare = selectedVideo.url;
    } else if (activeTab === 'RESUME') {
      urlToShare = resumeVideoUrl;
    }

    switch (platform) {
      case 'facebook':
        shareUrl = `https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(urlToShare)}&quote=${encodeURIComponent(shareText)}`;
        break;
      case 'instagram':
        shareUrl = `https://www.instagram.com/share?url=${encodeURIComponent(urlToShare)}`; // Instagram sharing works differently
        break;
      case 'tiktok':
        shareUrl = `https://www.tiktok.com/share?url=${encodeURIComponent(urlToShare)}`; // TikTok sharing URL
        break;
      case 'youtube':
        shareUrl = `https://www.youtube.com/share?url=${encodeURIComponent(urlToShare)}`; // YouTube sharing URL
        break;
      default:
        return;
    }
  };

  return (
    <div className="liste-video-container">
      <div className="liste-video-wrapper">
        <div className="liste-video-content">
          <nav className="tabs">
            <button
              className={activeTab === 'VIDEO' ? 'active' : ''}
              onClick={() => handleTabClick('VIDEO')}
            >
              Vidéo
            </button>
            <button
              className={activeTab === 'RESUME' ? 'active' : ''}
              onClick={() => handleTabClick('RESUME')}
            >
              Résumé
            </button>
            <button
              className={activeTab === 'STATSEQUIPES' ? 'active' : ''}
              onClick={() => handleTabClick('STATSEQUIPES')}
            >
              Stats Équipes
            </button>
            <button
              className={activeTab === 'STATSJOUEURS' ? 'active' : ''}
              onClick={() => handleTabClick('STATSJOUEURS')}
            >
              Stats Joueurs
            </button>
          </nav>

          <div className="tab-content">
            {activeTab === 'VIDEO' && selectedVideo ? (
              <div className="video-and-share-container">
                <video
                  className="liste-video-content-video"
                  src={selectedVideo.url}
                  poster="/ImageDeConverture-c.jpg"
                  controls
                >
                  <p>
