import React, { useState, useEffect, useRef } from 'react';
import { View, TextInput, Text, TouchableOpacity, ScrollView, Image, StyleSheet } from 'react-native';
import { searchClubs, fetchCompetitionsForClub } from './../api';

const SearchClub = ({ selectedClub, selectedCompetition, onSelectClub, onCompetitionSelected }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [clubs, setClubs] = useState([]);
  const [recentClubs, setRecentClubs] = useState([]);
  const [timeoutId, setTimeoutId] = useState(null);
  const [competitionNames, setCompetitionNames] = useState([]);
  const [selectedClubName, setSelectedClubName] = useState(null);

  const scrollViewRef = useRef(null); // Référence à ScrollView

  useEffect(() => {
    const savedRecentClubs = JSON.parse(localStorage.getItem('recentClubs')) || [];
    setRecentClubs(savedRecentClubs);
  }, []);

  useEffect(() => {
    if (selectedClub) {
      handleClubClick(selectedClub);
    }
  }, [selectedClub]);

  useEffect(() => {
    if (selectedCompetition) {
      handleCompetitionClick(selectedCompetition);
    }
  }, [selectedCompetition]);

  const handleSearch = async () => {
    if (searchTerm.trim() === '') {
      setClubs(recentClubs);
      return;
    }

    const clubData = await searchClubs(searchTerm);
    setClubs(clubData.slice(0, 30));
  };

  useEffect(() => {
    if (timeoutId) {
      clearTimeout(timeoutId);
    }

    const id = setTimeout(() => {
      handleSearch();
    }, 1000);

    setTimeoutId(id);

    return () => clearTimeout(id);
  }, [searchTerm]);

  const updateRecentClubs = (club) => {
    const updatedClubs = [club, ...recentClubs.filter(c => c.cl_no !== club.cl_no)].slice(0, 3);
    setRecentClubs(updatedClubs);
    localStorage.setItem('recentClubs', JSON.stringify(updatedClubs));
  };

  const handleClubClick = async (club) => {
    setSearchTerm('');
    setSelectedClubName(club.name); // Met à jour le club sélectionné
    onSelectClub(club);
    updateRecentClubs(club);

    // Faire défiler jusqu'en haut
    if (scrollViewRef.current) {
      scrollViewRef.current.scrollTo({ y: 0, animated: true });
    }

    let storedCompetitions = localStorage.getItem(`competitions_${club.cl_no}`);
    
    if (storedCompetitions) {
      storedCompetitions = JSON.parse(storedCompetitions);
    } else {
      storedCompetitions = await fetchCompetitionsForClub(club.cl_no);
      localStorage.setItem(`competitions_${club.cl_no}`, JSON.stringify(storedCompetitions));
    }
    
    setCompetitionNames(storedCompetitions);

    if (storedCompetitions.length > 0 && !selectedCompetition) {
      onCompetitionSelected(storedCompetitions[0]);
    }
  };

  const handleCompetitionClick = (competitionName) => {
    if (onCompetitionSelected) {
      onCompetitionSelected(competitionName);
    }

    // Faire défiler jusqu'en haut
    if (scrollViewRef.current) {
      scrollViewRef.current.scrollTo({ y: 0, animated: true });
    }
  };

  return (
    <ScrollView ref={scrollViewRef} style={styles.container}>
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          value={searchTerm}
          onChangeText={(text) => setSearchTerm(text)}
          placeholder="Rechercher un club"
        />
      </View>
      <Text style={styles.header}>Sélectionner un club</Text>
      <View>
        {clubs.length === 0 && searchTerm === '' ? (
          recentClubs.map((club) => (
            <TouchableOpacity
              key={club.cl_no}
              onPress={() => handleClubClick(club)}
              style={[styles.clubItem, club.name === selectedClubName && styles.selected]}
            >
              <Image source={{ uri: club.logo }} style={styles.logo} />
              <Text style={styles.clubName}>{club.name}</Text>
            </TouchableOpacity>
          ))
        ) : (
          clubs.map((club) => (
            <TouchableOpacity
              key={club.cl_no}
              onPress={() => handleClubClick(club)}
              style={[styles.clubItem, club.name === selectedClubName && styles.selected]}
            >
              <Image source={{ uri: club.logo }} style={styles.logo} />
              <Text style={styles.clubName}>{club.name}</Text>
            </TouchableOpacity>
          ))
        )}
      </View>

      {selectedClub && (
        <View>
          <Text style={styles.header}>Sélectionner une équipe</Text>
          <View>
            {competitionNames.length === 0 ? (
              <Text>Aucune compétition trouvée</Text>
            ) : (
              competitionNames.map((name, index) => (
                <TouchableOpacity 
                  key={index} 
                  onPress={() => handleCompetitionClick(name)}
                  style={[styles.competitionItem, name === selectedCompetition && styles.selected]}
                >
                  <Text>{name}</Text>
                </TouchableOpacity>
              ))
            )}
          </View>
        </View>
      )}
    </ScrollView>
  );
};


export default SearchClub;
