// config.js
let selectedClub = null;
let selectedCompetition = null;

const setSelectedClub = (club) => {
  selectedClub = club;
  localStorage.setItem('selectedClub', JSON.stringify(club)); // Enregistre dans localStorage
};

const getSelectedClub = () => {
  if (!selectedClub) {
    selectedClub = JSON.parse(localStorage.getItem('selectedClub'));
  }
  return selectedClub;
};

const setSelectedCompetition = (competition) => {
  selectedCompetition = competition;
  localStorage.setItem('selectedCompetition', competition); // Enregistre dans localStorage
};

const getSelectedCompetition = () => {
  if (!selectedCompetition) {
    selectedCompetition = localStorage.getItem('selectedCompetition');
  }
  return selectedCompetition;
};

module.exports = {
  setSelectedClub,
  getSelectedClub,
  setSelectedCompetition,
  getSelectedCompetition,
};
