// components/SearchClub.js
import React, { useState } from 'react';
import axios from 'axios';

const SearchClub = () => {
  const [clubName, setClubName] = useState('');
  const [clubData, setClubData] = useState(null);
  const [error, setError] = useState('');

  const handleSearch = async (e) => {
    e.preventDefault();
    const apiUrl = `https://api-dofa.prd-aws.fff.fr/api/clubs?clNom=${encodeURIComponent(clubName)}`;
    try {
      const response = await axios.get(apiUrl);
      setClubData(response.data);
      setError('');
    } catch (err) {
      setError('Erreur lors de la requête à l\'API');
      setClubData(null);
    }
  };

  return (
    <div>
      <h1>Recherche de Club</h1>
      <form onSubmit={handleSearch}>
        <label htmlFor="clubName">Nom du club:</label>
        <input
          type="text"
          id="clubName"
          value={clubName}
          onChange={(e) => setClubName(e.target.value)}
          required
        />
        <button type="submit">Rechercher</button>
      </form>
      {error && <p>{error}</p>}
      {clubData && (
        <div>
          <h2>Résultats:</h2>
          <pre>{JSON.stringify(clubData, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default SearchClub;
