import React, { useState } from 'react';
import './css/SearchClub.css';

const SearchClub = ({ onSelectClub }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [clubs, setClubs] = useState([]);
  const [selectedClub, setSelectedClub] = useState(null);

  const handleSearch = async () => {
    const response = await fetch(`https://api-dofa.prd-aws.fff.fr/api/clubs?clNom=${searchTerm}`);
    const data = await response.json();
    const clubData = data['hydra:member'].map(club => ({
      name: club.name,
      logo: club.logo
    }));
    setClubs(clubData);
  };

  const handleClubClick = (club) => {
    setSelectedClub(club);
    onSelectClub(club); // Notifie le parent du club sélectionné
  };

  return (
    <div className="search-club-container">
      <div className="input-container">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search for a club"
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <div>
        <h2>Selected Club:</h2>
        {selectedClub ? (
          <span>{selectedClub.name}</span>
        ) : (
          <p>No club selected</p>
        )}
      </div>
      <ul>
        {clubs.map((club, index) => (
          <li key={index} onClick={() => handleClubClick(club)}>
            <img src={club.logo} alt={`${club.name} logo`} />
            <span>{club.name}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SearchClub;
