// src/components/Profile.js
import React, { useEffect } from 'react'; // Importez useEffect pour configurer Google Sign-In
import { GoogleLogin } from '@react-oauth/google'; // Utilisation pour le web
import { sendIdTokenToBackend } from './api'; // Chemin vers votre fichier API
import './css/Profile.css';

const Profile = () => {
  useEffect(() => {
    // Cette configuration est nécessaire pour initialiser le Google Sign-In
    const initializeGoogleSignIn = () => {
      window.google.accounts.id.initialize({
        client_id: 'YOUR_CLIENT_ID.apps.googleusercontent.com', // Remplacez par votre Client ID
      });
    };

    // Appelez la fonction pour initialiser Google Sign-In
    initializeGoogleSignIn();
  }, []);

  const handleGoogleLoginSuccess = async (response) => {
    const idToken = response.credential; // Assurez-vous que credential existe

    try {
      const data = await sendIdTokenToBackend(idToken);
      console.log('Utilisateur vérifié avec succès:', data);
    } catch (error) {
      console.error('Erreur lors de la vérification du token Google:', error);
    }
  };

  const handleGoogleLoginError = (error) => {
    console.error('Erreur de connexion Google:', error);
  };

  return (
    <div className="profile-page">
      <div className="profile-container">
        <h1>Connectez-vous à votre compte</h1>

        {/* Email Input */}
        <div className="input-group">
          <label htmlFor="email">Adresse e-mail</label>
          <div className="inputs">
            <input type="email" id="email" placeholder="Entrez votre email" />
          </div>
        </div>

        {/* Password Input */}
        <div className="input-group">
          <label htmlFor="password">Mot de passe</label>
          <div className="inputs">
            <input type="password" id="password" placeholder="Entrez votre mot de passe" />
          </div>
        </div>

        {/* Login Button */}
        <div className="login-btn">Se connecter</div>

        {/* Divider */}
        <div className="divider">
          <span>ou</span>
        </div>

        {/* Google Login Button */}
        <div className="login-btn">
          <GoogleLogin
            onSuccess={handleGoogleLoginSuccess} // Fonction à appeler en cas de succès
            onError={handleGoogleLoginError} // Fonction à appeler en cas d'erreur
          />
        </div>
      </div>
    </div>
  );
};

export default Profile;
