import React, { useState, useEffect } from 'react';
import { fetchClassementJournees } from './../api'; // Importer la fonction depuis votre fichier API
import './css/ClassementsContent.css'; // Importer le fichier CSS

function ClassementsContent() {
  // État pour stocker les données du classement
  const [classements, setClassements] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Appel de l'API au montage du composant
  useEffect(() => {
    const fetchData = async () => {
      try {
        const competitionId = 426177;  // Exemple d'ID de compétition
        const phaseId = 1;
        const pouleId = 3;

        const data = await fetchClassementJournees(competitionId, phaseId, pouleId);
        setClassements(data);  // Mettre à jour l'état avec les données reçues
        setLoading(false);     // Désactiver le chargement
      } catch (error) {
        setError(error);       // Gérer l'erreur
        setLoading(false);     // Désactiver le chargement même en cas d'erreur
      }
    };

    fetchData();
  }, []); // [] signifie que l'effet ne s'exécute qu'une seule fois, au montage

  // Rendu conditionnel selon l'état
  if (loading) {
    return <div className="loading">Chargement des classements...</div>;
  }

  if (error) {
    return <div className="error">Erreur lors du chargement des classements : {error.message}</div>;
  }

  return (
    <div className="tab-content">
      <h2>Classements des Journées</h2>
      {classements.length > 0 ? (
        <table className="classements-table">
          <thead>
            <tr>
              <th>Équipe</th>
              <th>Points</th>
              <th>Joués</th>
              <th>Gagnés</th>
              <th>Nuls</th>
              <th>Perdus</th>
              <th>Buts Pour</th>
              <th>Buts Contre</th>
              <th>Différence de Buts</th>
            </tr>
          </thead>
          <tbody>
            {classements.map((journee, index) => (
              <tr key={index}>
                <td>{journee.equipe?.club?.short_name}</td>
                <td>{journee.point_count}</td>
                <td>{journee.total_games_count}</td>
                <td>{journee.won_games_count}</td>
                <td>{journee.draw_games_count}</td>
                <td>{journee.lost_games_count}</td>
                <td>{journee.goals_for_count}</td>
                <td>{journee.goals_against_count}</td>
                <td>{journee.goals_diff}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <div>Aucun classement disponible pour le moment.</div>
      )}
    </div>
  );
}

export default ClassementsContent;
