import React, { useState, useEffect, useRef } from 'react';
import './css/SearchClub.css';
import { searchClubs } from './../api';

const ClubSearch = ({ recentClubs, onClubSelect, selectedClubName }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [clubs, setClubs] = useState([]);
  const [timeoutId, setTimeoutId] = useState(null);
  const scrollViewRef = useRef(null);

  const handleSearch = async () => {
    if (searchTerm.trim() === '') {
      setClubs(recentClubs);
      return;
    }

    const clubData = await searchClubs(searchTerm);
    setClubs(clubData.slice(0, 30));
  };

  useEffect(() => {
    if (timeoutId) {
      clearTimeout(timeoutId);
    }

    const id = setTimeout(() => {
      handleSearch();
    }, 1000);

    setTimeoutId(id);

    return () => clearTimeout(id);
  }, [searchTerm]);

  return (
    <div ref={scrollViewRef} className="search-club-container">
      <div className="input-container">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Rechercher un club"
        />
      </div>
      <hr />
      <h4>SÃ©lectionner un club</h4>
      <div className="suggestions">suggestions</div>
      <ul>
        {(clubs.length === 0 && searchTerm === '') ? (
          recentClubs.map((club) => (
            <li
              key={club.cl_no}
              onClick={() => onClubSelect(club)}
              className={club.name === selectedClubName ? 'selected' : ''}
            >
              <img src={club.logo} alt={`${club.name} logo`} />
              <span>{club.name}</span>
            </li>
          ))
        ) : (
          clubs.map((club) => (
            <li
              key={club.cl_no}
              onClick={() => onClubSelect(club)}
              className={club.name === selectedClubName ? 'selected' : ''}
            >
              <img src={club.logo} alt={`${club.name} logo`} />
              <span>{club.name}</span>
            </li>
          ))
        )}
      </ul>
    </div>
  );
};

export default ClubSearch;
