import React, { createContext, useState, useEffect } from 'react';

// Créer le contexte
export const ClubContext = createContext();

// Fournir le contexte
export const ClubProvider = ({ children }) => {
  const [selectedClub, setSelectedClub] = useState(null);

  useEffect(() => {
    // Charger le club depuis le localStorage si disponible au démarrage
    const storedClub = localStorage.getItem('selectedClub');
    if (storedClub) {
      setSelectedClub(JSON.parse(storedClub));
    }
  }, []);

  const updateSelectedClub = (club) => {
    setSelectedClub(club);
    localStorage.setItem('selectedClub', JSON.stringify(club)); // Sauvegarder dans localStorage
  };

  return (
    <ClubContext.Provider value={{ selectedClub, updateSelectedClub }}>
      {children}
    </ClubContext.Provider>
  );
};
