import React from 'react';
import './css/SearchClub.css';

const CompetitionSearch = ({ competitionNames, onCompetitionSelect, selectedCompetition }) => {
  return (
    <div className="competition-list">
      <hr />
      <h4>Sélectionner une équipe</h4>
      <div className="suggestions">suggestions</div>
      <ul>
        {competitionNames.length === 0 ? (
          <li>Aucune compétition trouvée</li>
        ) : (
          competitionNames.map((name, index) => (
            <li 
              key={index} 
              onClick={() => onCompetitionSelect(name)}
              className={name === selectedCompetition ? 'selected' : ''}
            >
              {name}
            </li>
          ))
        )}
      </ul>
    </div>
  );
};

export default CompetitionSearch;
