import React, { useState, useEffect } from 'react';
import { fetchMatchesForClub } from './../api'; // Assurez-vous que le chemin d'importation est correct
import './MatchsContent.css'; // Importation du CSS

function MatchsContent({ cl_no }) {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadMatches = async () => {
      setLoading(true);
      try {
        const data = await fetchMatchesForClub(cl_no);
        setMatches(data);
      } catch (error) {
        setError('Erreur lors de la récupération des matchs.');
      } finally {
        setLoading(false);
      }
    };

    loadMatches();
  }, [cl_no]);

  if (loading) {
    return <div className="loading">Chargement des matchs...</div>;
  }

  if (error) {
    return <div className="error">Erreur : {error}</div>;
  }

  return (
    <div className="tab-content">
      <h2>Matchs</h2>
      {matches.length === 0 ? (
        <p className="error">Aucun match trouvé.</p>
      ) : (
        <div>
          {matches.map(match => (
            <div key={match.id} className="match-item">
              <div className="match-date">
                {new Date(match.date).toLocaleDateString()} {/* Affichage de la date */}
              </div>
              <div className="match-details">
                <span className="match-details">{match.homeTeam}</span>
              </div>
              <div className="match-score">
                {match.home_score} - {match.away_score}
              </div>
              <div className="match-details">
                <span className="match-details">{match.awayTeam}</span>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default MatchsContent;
