import React, { useEffect, useState } from 'react';
import './css/Resultat.css';  // Assurez-vous d'importer ce fichier CSS
import config from '../config';  // Import du fichier config.js

// Importer les composants de contenu
import MatchsContent from './Resultat/MatchsContent';
import ClassementsContent from './Resultat/ClassementsContent';
import StatsContent from './Resultat/StatsContent';

function Resultat() {
  const [activeTab, setActiveTab] = useState('MATCHS');
  const [selectedClub, setSelectedClub] = useState(config.getSelectedClub());  // Récupérer le club sélectionné
  const [selectedCompetition, setSelectedCompetition] = useState(config.getSelectedCompetition());  // Récupérer la compétition sélectionnée

  useEffect(() => {
    console.log('Resultat component mounted or dependencies changed');
  }, []);

  const renderContent = () => {
    switch (activeTab) {
      case 'MATCHS':
        return <MatchsContent club={selectedClub} competition={selectedCompetition} />;
      case 'CLASSEMENTS':
        return <ClassementsContent club={selectedClub} competition={selectedCompetition} />;
      case 'STATS':
        return <StatsContent club={selectedClub} competition={selectedCompetition} />;
      default:
        return null;
    }
  };

  return (
    <div className="resultat-container">
      {/* Affichage du club et de la compétition sélectionnés */}
      <header className="selected-info">
        {selectedClub ? (
          <>
            <img src={selectedClub.logo} alt={`${selectedClub.name} logo`} className="club-logo" />
            <div className="club-info">
              <h2>{selectedClub.name}</h2>
              {selectedCompetition && <h3>{selectedCompetition}</h3>}
            </div>
          </>
        ) : (
          <p>Aucun club sélectionné</p>
        )}
      </header>

      {/* Menu avec onglets */}
      <nav>
        <button
          className={activeTab === 'MATCHS' ? 'active' : ''}
          onClick={() => setActiveTab('MATCHS')}
        >
          MATCHS
        </button>
        <button
          className={activeTab === 'CLASSEMENTS' ? 'active' : ''}
          onClick={() => setActiveTab('CLASSEMENTS')}
        >
          CLASSEMENTS
        </button>
        <button
          className={activeTab === 'STATS' ? 'active' : ''}
          onClick={() => setActiveTab('STATS')}
        >
          STATS
        </button>
      </nav>

      {/* Affichage du contenu en fonction de l'onglet sélectionné */}
      {renderContent()}
    </div>
  );
}

export default Resultat;
