import React, { useState, useEffect } from 'react';
import { fetchClassementJournees } from './../api'; // Importer la fonction depuis votre fichier API
import './css/ClassementsContent.css';
import config from '../../config';  // Import du fichier config.js

function ClassementsContent() {
  const [classements, setClassements] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [selectedClub, setSelectedClub] = useState(config.getSelectedClub());  // Initialiser avec le club sélectionné
  const [selectedCompetition, setSelectedCompetition] = useState(config.getSelectedCompetition());  // Initialiser avec la compétition sélectionnée

  useEffect(() => {
    const fetchData = async () => {
      try {
        const competitionId = 426177;
        const phaseId = 1;
        const pouleId = 2;

        const data = await fetchClassementJournees(competitionId, phaseId, pouleId);
        setClassements(data);
        setLoading(false);
      } catch (error) {
        setError(error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Chargement des classements...</div>;
  }

  if (error) {
    return <div>Erreur lors du chargement des classements : {error.message}</div>;
  }

  return (
    <div className="classement-content">
      {classements.length > 0 ? (
        <table className="classement-table">
          <thead>
            <tr>
              <th></th> {/* Nouvelle colonne pour la position */}
              <th>Club</th> {/* Classe ajoutée ici */}
              <th>Pts</th>
              <th>MJ</th>
              <th>G</th>
              <th>N</th>
              <th>P</th>
            </tr>
          </thead>
          <tbody>
            {classements.map((journee, index) => (
              <tr key={index}>
                <td>{index + 1}</td> {/* Affiche la position en fonction de l'index */}
                <td className="team-column">{journee.teamName}</td>
                <td className="points-column">{journee.points}</td>
                <td>{journee.totalGames}</td>
                <td>{journee.wonGames}</td>
                <td>{journee.drawGames}</td>
                <td>{journee.lostGames}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="error">Aucun classement disponible pour le moment.</p>
      )}
    </div>
  );
}

export default ClassementsContent;
