import React, { useState, useEffect, useCallback } from 'react';
import './css/ListeVideo.css';
import { getSelectedClub } from './../../config'; // Importer la fonction pour obtenir le club sélectionné

const ListeVideo = () => {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null); // Vidéo sélectionnée
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('MATCHS'); // Onglet actif (MATCHS par défaut)

  // Fonction pour rechercher les vidéos
  const handleSearch = useCallback(async () => {
    setLoading(true);
    setError(null);
    const club = getSelectedClub();
    const folder = club ? club.name : '';
    const url = `https://ia-sport.oa.r.appspot.com/api/videos?folder=${encodeURIComponent(folder)}`;

    if (folder) {
      try {
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error('Erreur lors de la récupération des vidéos');
        }
        const data = await response.json();
        setVideos(data.videos || []);
        setSelectedVideo(data.videos[0] || null); // Sélectionner la première vidéo par défaut
      } catch (error) {
        setError('Impossible de charger les vidéos. Veuillez réessayer plus tard.');
        console.error("Erreur lors de la recherche des vidéos:", error);
        setVideos([]);
      } finally {
        setLoading(false);
      }
    } else {
      setVideos([]);
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    handleSearch();
  }, [handleSearch]);

  const handleVideoSelect = (video) => {
    setSelectedVideo(video);
  };

  const handleDoubleClick = (event) => {
    const videoElement = event.target;

    if (videoElement.requestFullscreen) {
      videoElement.requestFullscreen();
    } else if (videoElement.mozRequestFullScreen) { // Firefox
      videoElement.mozRequestFullScreen();
    } else if (videoElement.webkitRequestFullscreen) { // Chrome, Safari and Opera
      videoElement.webkitRequestFullscreen();
    } else if (videoElement.msRequestFullscreen) { // IE/Edge
      videoElement.msRequestFullscreen();
    }
  };

  const handleClick = (event) => {
    const videoElement = event.target;
    if (videoElement.paused) {
      videoElement.play();
    } else {
      videoElement.pause();
    }
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="liste-video-container">
      {loading && <div className="loader">Chargement...</div>}
      {error && <div className="error-message">{error}</div>}
      {!loading && !error && (
        <div className="liste-video-wrapper">
          {/* Menu avec onglets */}
          <nav className="video-tabs">
            <button
              className={activeTab === 'MATCHS' ? 'active' : ''}
              onClick={() => handleTabClick('MATCHS')}
            >
              MATCHS
            </button>
            <button
              className={activeTab === 'CLASSEMENTS' ? 'active' : ''}
              onClick={() => handleTabClick('CLASSEMENTS')}
            >
              CLASSEMENTS
            </button>
            <button
              className={activeTab === 'STATS' ? 'active' : ''}
              onClick={() => handleTabClick('STATS')}
            >
              STATS
            </button>
            {/* Ajoutez ici des boutons pour les autres onglets */}
          </nav>

          {/* Colonne de gauche: Liste des titres de vidéos */}
          <div className="liste-video-sidebar">
            <ul className="liste-video-titles">
              {videos.length > 0 ? (
                videos
                  .filter(video => video.tab === activeTab) // Filtrer les vidéos selon l'onglet actif
                  .map((video, index) => (
                    <li
                      key={index}
                      className={`liste-video-title ${selectedVideo === video ? 'selected' : ''}`}
                      onClick={() => handleVideoSelect(video)}
                    >
                      {video.name}
                    </li>
                  ))
              ) : (
                <p className="no-videos">Aucune vidéo disponible</p>
              )}
            </ul>
          </div>

          {/* Colonne de droite: Vidéo sélectionnée */}
          <div className="liste-video-content">
            {selectedVideo ? (
              <video 
                className="liste-video-video" 
                src={selectedVideo.url} 
                controls
                onClick={handleClick}
                onDoubleClick={handleDoubleClick}
              >
                <p>Your browser doesn't support HTML5 video. Here is a <a href={selectedVideo.url}>link to the video</a> instead.</p>
              </video>
            ) : (
              <p className="no-videos">Sélectionnez une vidéo à lire</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default ListeVideo;
