import React, { useState, useEffect } from 'react';
import './css/Home.css';

function Home() {
  const [isRecording, setIsRecording] = useState(false);
  const [timeElapsed, setTimeElapsed] = useState(0);
  const [playbackURI, setPlaybackURI] = useState('');

  useEffect(() => {
    let timer;
    if (isRecording) {
      timer = setInterval(() => {
        setTimeElapsed(prevTime => prevTime + 1);
      }, 1000);
    } else {
      clearInterval(timer);
    }

    return () => clearInterval(timer);
  }, [isRecording]);

  const handleStart = async () => {
    try {
      const response = await fetch('https://ia-sport.oa.r.appspot.com/api/start-recording', {
        method: 'PUT'
      });

      if (!response.ok) {
        throw new Error('Failed to start recording');
      }

      setIsRecording(true);
      setTimeElapsed(0); // Réinitialiser le chronomètre au démarrage
      alert('Recording started successfully');
    } catch (error) {
      console.error('Error:', error);
      alert('Failed to start recording');
    }
  };

  const handleStop = async () => {
    try {
      const response = await fetch('https://ia-sport.oa.r.appspot.com/api/stop-recording', {
        method: 'PUT'
      });

      if (!response.ok) {
        throw new Error('Failed to stop recording');
      }

      // Récupérer l'URI de lecture après l'arrêt de l'enregistrement
      const playbackResponse = await fetch('https://ia-sport.oa.r.appspot.com/api/search', {
        method: 'GET'
      });

      if (!playbackResponse.ok) {
        throw new Error('Failed to fetch playback URI');
      }

      const playbackData = await playbackResponse.json();
      const { playbackURI } = playbackData;
      setPlaybackURI(playbackURI);

      // Poster les données à l'API d'upload
      const uploadResponse = await fetch('https://ia-sport.oa.r.appspot.com/api/upload', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          filename: 'videosofianso.mp4',
          cameraRtspUrl: playbackURI,
          directory: 'FC Vidauban',
        }),
      });

      if (!uploadResponse.ok) {
        throw new Error('Failed to upload video');
      }

      alert('Recording stopped and video uploaded successfully');
      setIsRecording(false);
    } catch (error) {
      console.error('Error:', error);
      alert('Failed to stop recording or upload video');
    }
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
  };

  return (
    <div>
      <h2>Home Page</h2>
      <p>Welcome to the Home Page!</p>

      <div className="button-container">
        <button className="btn-start" onClick={handleStart}>Start</button>
        <button className="btn-stop" onClick={handleStop}>Stop</button>
      </div>

      {isRecording && (
        <div className="timer">
          <p>Recording time: {formatTime(timeElapsed)}</p>
        </div>
      )}
    </div>
  );
}

export default Home;
