import React, { useState, useEffect } from 'react';
import { FaPlay, FaStop } from 'react-icons/fa'; // Importer les icônes
import './css/Home.css';

function Home() {
  const [isRecording, setIsRecording] = useState(false);
  const [timeElapsed, setTimeElapsed] = useState(0);
  const [filename, setFilename] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    let timer;
    if (isRecording) {
      timer = setInterval(() => {
        setTimeElapsed(prevTime => prevTime + 1);
      }, 1000);
    } else {
      clearInterval(timer);
    }

    return () => clearInterval(timer);
  }, [isRecording]);

  const handleStart = async () => {
    // Code pour démarrer l'enregistrement
    setIsRecording(true);
    setTimeElapsed(0); // Réinitialiser le chronomètre au démarrage
    setMessage('Enregistrement démarré avec succès');
  };

  const handleStop = async () => {
    // Code pour arrêter l'enregistrement
    setIsRecording(false);
    setMessage('Enregistrement arrêté avec succès');
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
  };

  return (
    <div className="home-container">
      <h2>Démarrer un enregistrement</h2>
      <br />
      <div className="input-container">
        <input
          type="text"
          id="filename"
          value={filename}
          onChange={(e) => setFilename(e.target.value)}
          placeholder="OM - PSG"
        />
      </div>
      <div className="button-container">
        <button className="button-icon" onClick={handleStart} disabled={isRecording}>
          <FaPlay />
        </button>
        <button className="button-icon" onClick={handleStop} disabled={!isRecording}>
          <FaStop />
        </button>
      </div>
      {isRecording && (
        <div className="timer">
          <p>Temps d'enregistrement : {formatTime(timeElapsed)}</p>
        </div>
      )}
      {message && (
        <div className="message">
          <p>{message}</p>
        </div>
      )}
    </div>
  );
}

export default Home;
