import React, { useState, useEffect, useRef } from 'react';
import { fetchMatchesForClub } from './../api'; // Assurez-vous que le chemin d'importation est correct
import './css/MatchsContent.css'; // Importation du CSS
import config from '../../config';  // Import du fichier config.js

function MatchsContent({ cl_no }) {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const matchRefs = useRef([]); // Références pour chaque match
  const [selectedClub, setSelectedClub] = useState(config.getSelectedClub());  // Récupérer le club sélectionné
  const [selectedCompetition, setSelectedCompetition] = useState(config.getSelectedCompetition());  // Récupérer la compétition sélectionnée

  useEffect(() => {
    const loadMatches = async () => {
      setLoading(true);
      try {
        const data = await fetchMatchesForClub(selectedClub.cl_no);
        setMatches(data);
      } catch (error) {
        setError('Erreur lors de la récupération des matchs.');
      } finally {
        setLoading(false);
      }
    };

    loadMatches();
  }, [cl_no, selectedClub]);

  useEffect(() => {
    if (matches.length > 0) {
      const today = new Date();
      let latestDateBeforeToday = null;
      matches.forEach(match => {
        const matchDate = new Date(match.date);
        if (matchDate < today) {
          if (!latestDateBeforeToday || matchDate > latestDateBeforeToday) {
            latestDateBeforeToday = matchDate;
          }
        }
      });

      if (latestDateBeforeToday) {
        const matchIndex = matches.findIndex(
          match => new Date(match.date).getTime() === latestDateBeforeToday.getTime()
        );
        if (matchIndex >= 0) {
          matchRefs.current[matchIndex].scrollIntoView({
            behavior: 'smooth',
            block: 'start',
          });
        }
      }
    }
  }, [matches]);

  // Filtrer les matchs pour ne garder que ceux où l'équipe à domicile est celle du club sélectionné
  const filteredMatches = matches.filter(match => (match.homeTeam === selectedClub.name && match.homeCompetitionName === selectedCompetition ) || (match.awayTeam === selectedClub.name && match.awayCompetitionName === selectedCompetition));

  if (loading) {
    return <div className="loading">Chargement des matchs...</div>;
  }

  if (error) {
    return <div className="error">Erreur : {error}</div>;
  }

  return (
    <div className="tab-content">
      {filteredMatches.length === 0 ? (
        <p className="error">Aucun match trouvé pour {selectedClub.name} à domicile.</p>
      ) : (
        <div>
          {filteredMatches.map((match, index) => (
            <div
              key={match.id}
              className="match-item"
              ref={el => matchRefs.current[index] = el} // Assignation de la ref à chaque élément
            >
              <div className="match-date">
                {new Date(match.date).toLocaleDateString()} {/* Affichage de la date */}
                - {match.time} - {match.competitionName} - {match.homeCompetitionName} - {match.awayCompetitionName}
              </div>
              <div className="match-content">
                <div className="match-details-team">
                  <img src={match.homeLogo} alt={`${match.homeTeam} logo`} className="team-logo" />
                  {match.homeTeam} {/* Nom de l'équipe à domicile */}
                </div>
                <div className="match-score">
                  {match.home_score} {/* Scores */}
                </div>
                <div className="match-details-team">
                  <img src={match.awayLogo} alt={`${match.awayTeam} logo`} className="team-logo" />
                  {match.awayTeam} {/* Nom de l'équipe à l'extérieur */}
                </div>
                <div className="match-score">
                  {match.away_score} {/* Scores */}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default MatchsContent;
