import React, { useState } from 'react';
import SearchClub from './SearchClub';

const Explore = ({ onSelectClub, selectedClub }) => {
  const [selectedCompetition, setSelectedCompetition] = useState(null);

  const handleCompetitionSelected = (competitionName) => {
    setSelectedCompetition(competitionName);
  };

  return (
    <div>
      <SearchClub 
        onSelectClub={onSelectClub} 
        onCompetitionSelected={handleCompetitionSelected} // Passer la fonction de rappel
      />
      <div>
        {selectedCompetition && (
          <div>
            <h3>Compétition sélectionnée :</h3>
            <p>{selectedCompetition}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Explore;
