import React from 'react';
import { Link } from 'react-router-dom';
import './css/Home.css';

function Navbar({ selectedClub, selectedCompetition }) {
  // Désactiver les onglets si le club ou la compétition n'est pas sélectionné
  const isDisabled = !selectedClub || !selectedCompetition;

  return (
    <nav className="navbar">
      <ul>
        {/* Onglet Explore toujours actif */}
        <li>
          <Link to="/explore" className="nav-link">Explore</Link>
        </li>

        {/* Onglets désactivés si les conditions ne sont pas remplies */}
        <li>
          <Link
            to="/dashboard"
            className={`nav-link ${isDisabled ? 'disabled' : ''}`}
            onClick={(e) => isDisabled && e.preventDefault()}
          >
            Dashboard
          </Link>
        </li>

        <li>
          <Link
            to="/settings"
            className={`nav-link ${isDisabled ? 'disabled' : ''}`}
            onClick={(e) => isDisabled && e.preventDefault()}
          >
            Settings
          </Link>
        </li>
      </ul>
    </nav>
  );
}

export default Navbar;
