// App.js

import React, { useEffect } from 'react';
import { View, Text } from 'react-native';
import Orientation from 'react-native-orientation-locker';

const App = () => {
  useEffect(() => {
    // Verrouille l'orientation en mode paysage
    Orientation.lockToLandscape();

    // Cleanup pour déverrouiller l'orientation lorsque le composant est démonté
    return () => {
      Orientation.unlockAllOrientations();
    };
  }, []);

  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center', backgroundColor: '#010E1E' }}>
      <Text style={{ color: '#ffffff' }}>Bienvenue dans votre application en mode paysage !</Text>
    </View>
  );
};

export default App;
