import React, { useState, useEffect } from 'react';
import { searchClubs, fetchCompetitionsForClub } from './api';
import './css/SearchClub.css';

const SearchClub = ({ onSelectClub }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [clubs, setClubs] = useState([]);
  const [recentClubs, setRecentClubs] = useState([]);
  const [timeoutId, setTimeoutId] = useState(null);
  const [selectedClub, setSelectedClub] = useState(null);

  const handleSearch = async () => {
    if (searchTerm.trim() === '') {
      setClubs(recentClubs);
      return;
    }

    const clubData = await searchClubs(searchTerm);
    setClubs(clubData.slice(0, 3)); // Limiter les résultats à 3
  };

  useEffect(() => {
    if (timeoutId) {
      clearTimeout(timeoutId);
    }

    const id = setTimeout(() => {
      handleSearch();
    }, 1000);

    setTimeoutId(id);

    return () => clearTimeout(id);
  }, [searchTerm]);

  useEffect(() => {
    const savedRecentClubs = JSON.parse(localStorage.getItem('recentClubs')) || [];
    setRecentClubs(savedRecentClubs);
  }, []);

  const updateRecentClubs = (club) => {
    const updatedClubs = [club, ...recentClubs.filter(c => c.cl_no !== club.cl_no)].slice(0, 3);
    setRecentClubs(updatedClubs);
    localStorage.setItem('recentClubs', JSON.stringify(updatedClubs));
  };

  const handleClubClick = async (club) => {
    setSearchTerm("");
    setSelectedClub(club);
    updateRecentClubs(club);
    
    // Appel API pour obtenir les noms des compétitions du club sélectionné
    const names = await fetchCompetitionsForClub(club.cl_no);
    onSelectClub(club, names); // Passe le club sélectionné et les noms des compétitions au parent
  };

  return (
    <div className="search-club-container">
      <div className="input-container">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Rechercher un club"
        />
      </div>
      <div className="suggestions">suggestions</div>
      <ul>
        {clubs.length === 0 && searchTerm === '' ? (
          recentClubs.map((club) => (
            <li key={club.cl_no} onClick={() => handleClubClick(club)}>
              <img src={club.logo} alt={`${club.name} logo`} />
              <span>{club.name}</span>
            </li>
          ))
        ) : (
          clubs.map((club) => (
            <li key={club.cl_no} onClick={() => handleClubClick(club)}>
              <img src={club.logo} alt={`${club.name} logo`} />
              <span>{club.name}</span>
            </li>
          ))
        )}
      </ul>
    </div>
  );
};

export default SearchClub;
