import React, { useState } from 'react';
import SearchClub from './SearchClub';

const Explore = () => {
  const [selectedClub, setSelectedClub] = useState(null);

  const handleClubSelection = (club) => {
    setSelectedClub(club); // Met à jour l'état avec le club sélectionné
  };

  return (
    <div>
      <h1>Explore</h1>
      <SearchClub onSelectClub={handleClubSelection} />
      <div>
        <h2>Selected Club:</h2>
        {selectedClub ? (
          <span>{selectedClub.name}</span> // Affichage du nom du club sélectionné
        ) : (
          <p>No club selected</p>
        )}
      </div>
    </div>
  );
};

export default Explore;
