// config.js
let selectedClub = null;
let selectedClubNo = null;
let selectedCompetition = null;

const setSelectedClub = (club) => {
  selectedClub = club;
  selectedClubNo = club.cl_no;  // Stocker le cl_no
};

const getSelectedClub = () => selectedClub;
const getSelectedClubNo = () => selectedClubNo;

const setSelectedCompetition = (competition) => {
  selectedCompetition = competition;
};

const getSelectedCompetition = () => selectedCompetition;

module.exports = {
  setSelectedClub,
  getSelectedClub,
  getSelectedClubNo,  // Exporter le cl_no
  setSelectedCompetition,
  getSelectedCompetition
};
