import React, { useState } from 'react';
import './css/ListeVideo.css';

const ListeVideo = ({ selectedVideo, resumeVideoUrl }) => {
  const [activeTab, setActiveTab] = useState('VIDEO');
  const [shareVisible, setShareVisible] = useState(false);

  const handleSave = () => {
    console.log("Données enregistrées !");
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  const handleDownload = () => {
    const link = document.createElement('a');
    link.href = 'URL_DU_FICHIER_A_TELECHARGER';
    link.download = 'NOM_DU_FICHIER.extension';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const toggleShareButtons = () => {
    setShareVisible((prev) => !prev);
  };

  const shareOnSocialMedia = (platform) => {
    const urlToShare = activeTab === 'VIDEO' ? selectedVideo.url : resumeVideoUrl;
    const shareText = `Check out this video: ${urlToShare}`;
    
    let shareUrl;
    switch (platform) {
      case 'facebook':
        shareUrl = `https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(urlToShare)}&quote=${encodeURIComponent(shareText)}`;
        break;
      case 'instagram':
        shareUrl = `https://www.instagram.com/share?url=${encodeURIComponent(urlToShare)}`; // Instagram sharing works differently
        break;
      case 'tiktok':
        shareUrl = `https://www.tiktok.com/share?url=${encodeURIComponent(urlToShare)}`; // TikTok sharing URL
        break;
      case 'youtube':
        shareUrl = `https://www.youtube.com/share?url=${encodeURIComponent(urlToShare)}`; // YouTube sharing URL
        break;
      default:
        return;
    }
    
    window.open(shareUrl, '_blank');
  };

  return (
    <div className="liste-video-container">
      <div className="liste-video-wrapper">
        <div className="liste-video-content">
          <nav className="tabs">
            <button className={activeTab === 'VIDEO' ? 'active' : ''} onClick={() => handleTabClick('VIDEO')}>
              Vidéo
            </button>
            <button className={activeTab === 'RESUME' ? 'active' : ''} onClick={() => handleTabClick('RESUME')}>
              Résumé
            </button>
            <button className={activeTab === 'STATSEQUIPES' ? 'active' : ''} onClick={() => handleTabClick('STATSEQUIPES')}>
              Stats Équipes
            </button>
            <button className={activeTab === 'STATSJOUEURS' ? 'active' : ''} onClick={() => handleTabClick('STATSJOUEURS')}>
              Stats Joueurs
            </button>
          </nav>

          <div className="tab-content">
            {activeTab === 'VIDEO' && selectedVideo ? (
              <div className="video-and-share-container">
                <video className="liste-video-content-video" src={selectedVideo.url} poster="/ImageDeConverture-c.jpg" controls>
                  <p>
                    Votre navigateur ne supporte pas la vidéo HTML5. Voici un{' '}
                    <a href={selectedVideo.url}>lien vers la vidéo</a> à la place.
                  </p>
                </video>

                <div className="share-buttons-container">
                  <button className="option-button" onClick={handleSave}>
                    <i className="fa-regular fa-eye"></i>
                  </button>
                  <button className="option-button" onClick={handleSave}>
                    <i className="fa-regular fa-bookmark"></i>
                  </button>
                  <button className="option-button" onClick={handleDownload}>
                    <i className="fas fa-download"></i>
                  </button>
                  <button className="option-button" onClick={toggleShareButtons}>
                    <i className="fas fa-share-alt"></i>
                  </button>
                  <div className={`social-media-buttons ${shareVisible ? 'social-media-visible' : ''}`}>
                    <button className="facebook-button" onClick={() => shareOnSocialMedia('facebook')}>
                      <i className="fab fa-facebook-f"></i>
                    </button>
                    <button className="instagram-button" onClick={() => shareOnSocialMedia('instagram')}>
                      <i className="fab fa-instagram"></i>
                    </button>
                    <button className="tiktok-button" onClick={() => shareOnSocialMedia('tiktok')}>
                      <i className="fab fa-tiktok"></i>
                    </button>
                    <button className="youtube-button" onClick={() => shareOnSocialMedia('youtube')}>
                      <i className="fab fa-youtube"></i>
                    </button>
                  </div>
                </div>
              </div>
            ) : activeTab === 'RESUME' ? (
              <div className="video-and-share-container">
                <video className="liste-video-content-video" src={resumeVideoUrl} poster="/ImageDeConverture-r.jpg" controls>
                  <p>
                    Votre navigateur ne supporte pas la vidéo HTML5. Voici un{' '}
                    <a href={resumeVideoUrl}>lien vers la vidéo</a> à la place.
                  </p>
                </video>

                <div className="share-buttons-container">
                  <button className="option-button" onClick={handleSave}>
                    <i className="fa-regular fa-eye"></i>
                  </button>
                  <button className="option-button" onClick={handleSave}>
                    <i className="fa-regular fa-bookmark"></i>
                  </button>
                  <button className="option-button" onClick={handleDownload}>
                    <i className="fas fa-download"></i>
                  </button>
                  <button className="option-button" onClick={toggleShareButtons}>
                    <i className="fas fa-share-alt"></i>
                  </button>
                  <div className={`social-media-buttons ${shareVisible ? 'social-media-visible' : ''}`}>
                    <button className="facebook-button" onClick={() => shareOnSocialMedia('facebook')}>
                      <i className="fab fa-facebook-f"></i>
                    </button>
                    <button className="instagram-button" onClick={() => shareOnSocialMedia('instagram')}>
                      <i className="fab fa-instagram"></i>
                    </button>
                    <button className="tiktok-button" onClick={() => shareOnSocialMedia('tiktok')}>
                      <i className="fab fa-tiktok"></i>
                    </button>
                    <button className="youtube-button" onClick={() => shareOnSocialMedia('youtube')}>
                      <i className="fab fa-youtube"></i>
                    </button>
                  </div>
                </div>
              </div>
            ) : activeTab === 'STATSEQUIPES' ? (
              <div className="video-comments">
                <img src="/stat equipe.jpg" alt="Stats Équipes" className="stats-equipe-image" />
              </div>
            ) : activeTab === 'STATSJOUEURS' ? (
              <div className="video-others">
                <img src="/stat joueur.jpg" alt="Stats Joueurs" className="stats-equipe-image" />
              </div>
            ) : null}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ListeVideo;
