import React, { useState } from 'react';

const SearchVideo = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [videos, setVideos] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await fetch(`https://ia-sport.oa.r.appspot.com/api/videos?folder=${encodeURIComponent(searchTerm)}`);
      const data = await response.json();
      setVideos(data.videos);
    } catch (error) {
      console.error("Erreur lors de la recherche des vidéos:", error);
    }
  };

  return (
    <div className="search-video-container">
      <div className="input-container">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search for a folder"
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <ul>
        {videos.map((video, index) => (
          <li key={index}>
            <video width="320" height="240" controls>
              <source src={video} type="video/mp4" />
              <p>Your browser doesn't support HTML5 video. Here is a <a href={video}>link to the video</a> instead.</p>
            </video>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ListeVideo;
