import React, { useState, useEffect } from 'react';
import { FaPlay, FaStop } from 'react-icons/fa';
import './css/Home.css';
import { searchClubs } from './api';

function Home({ selectedClub }) {
  const [isRecording, setIsRecording] = useState(false);
  const [timeElapsed, setTimeElapsed] = useState(0);
  const [playbackURI, setPlaybackURI] = useState('');
  const [videoDuration, setVideoDuration] = useState(0);
  const [filename, setFilename] = useState('');
  const [message, setMessage] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [timeoutId, setTimeoutId] = useState(null);
  const [selectedClubInfo, setSelectedClubInfo] = useState(null); // Contient le nom et le logo du club sélectionné

  useEffect(() => {
    let timer;
    if (isRecording) {
      timer = setInterval(() => {
        setTimeElapsed(prevTime => prevTime + 1);
      }, 1000);
    } else {
      clearInterval(timer);
    }

    return () => clearInterval(timer);
  }, [isRecording]);

  const handleInputChange = (e) => {
    setFilename(e.target.value);

    if (timeoutId) {
      clearTimeout(timeoutId);
    }

    const newTimeoutId = setTimeout(async () => {
      const results = await searchClubs(e.target.value);
      setSearchResults(results);
    }, 500); // Déclencher la recherche après 500ms

    setTimeoutId(newTimeoutId);
  };

  const handleResultClick = (result) => {
    setFilename(result.name);
    setSelectedClubInfo(result); // Stocker le nom et le logo du club sélectionné
    setSearchResults([]);
  };

  const handleButtonClick = async () => {
    if (isRecording) {
      // Arrêter l'enregistrement
      setFilename('');
      try {
        setIsRecording(false);
        const response = await fetch('https://ia-sport.oa.r.appspot.com/api/stop-recording', {
          method: 'PUT'
        });

        if (!response.ok) {
          throw new Error('Échec de l\'arrêt de l\'enregistrement');
        }

        const playbackResponse = await fetch('https://ia-sport.oa.r.appspot.com/api/search', {
          method: 'GET'
        });

        if (!playbackResponse.ok) {
          throw new Error('Échec de la récupération de l\'URI de lecture');
        }

        const playbackData = await playbackResponse.json();
        const { playbackURI, videoDuration } = playbackData;
        setPlaybackURI(playbackURI);
        setVideoDuration(videoDuration);

        const directory = selectedClub ? selectedClub.name : 'Unknown Club';

        const uploadResponse = await fetch('https://ia-sport.oa.r.appspot.com/api/upload', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            filename: directory + `${filename}.mp4`,
            cameraRtspUrl: playbackURI,
            directory: directory,
            duration: videoDuration,
          }),
        });

        if (!uploadResponse.ok) {
          throw new Error('Échec du téléchargement de la vidéo');
        }

        setMessage('Enregistrement arrêté et vidéo téléchargée avec succès');
      } catch (error) {
        console.error('Erreur :', error);
        setMessage('Échec de l\'arrêt de l\'enregistrement ou du téléchargement de la vidéo');
      }
    } else {
      try {
        const response = await fetch('https://ia-sport.oa.r.appspot.com/api/start-recording', {
          method: 'PUT'
        });

        if (!response.ok) {
          throw new Error('Échec du démarrage de l\'enregistrement');
        }

        setIsRecording(true);
        setTimeElapsed(0);
        setMessage('Enregistrement démarré avec succès');
      } catch (error) {
        console.error('Erreur :', error);
        setMessage('Échec du démarrage de l\'enregistrement');
      }
    }
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
  };

  return (
    <div className="home-container">
      <h2>{isRecording ? 'Enregistrement en cours' : 'Démarrer un enregistrement'}</h2>
      {selectedClub && (
        <div className="selected-club-info">
          <img src={selectedClub.logo} alt={selectedClub.name} className="selected-club-logo" />
          <span className="selected-club-name">{selectedClub.name}</span>
        </div>
      )}
      <div className="input-container">
        <div className="selected-club-info">
          {selectedClubInfo && (
            <>
              <img src={selectedClubInfo.logo} alt="Selected club logo" className="selected-club-logo" />
              <span className="selected-club-name">{selectedClubInfo.name}</span>
            </>
          )}
          {!selectedClubInfo && (
            <input
              type="text"
              id="filename"
              value={filename}
              onChange={handleInputChange}
              placeholder="OM - PSG"
            />
          )}
        </div>
        {searchResults.length > 0 && (
          <ul className="search-results">
            {searchResults.map((result, index) => (
              <li key={index} onClick={() => handleResultClick(result)}>
                <img src={result.logo} alt={result.name} className="result-logo" />
                {result.name}
              </li>
            ))}
          </ul>
        )}
      </div>
      <button
        className={`button ${isRecording ? 'stop' : ''} ${!filename ? 'disabled' : ''}`}
        onClick={handleButtonClick}
        disabled={!filename && !isRecording}
      >
        {isRecording ? <FaStop /> : <FaPlay />}
      </button>
      {isRecording && (
        <div className="timer">
          <p>Temps d'enregistrement : {formatTime(timeElapsed)}</p>
        </div>
      )}
      {message && (
        <div className="message">
          <p>{message}</p>
        </div>
      )}
    </div>
  );
}

export default Home;
